     1→<template>
     2→  <AppDialogue
     3→    ref="modal"
     4→    class="device-access-dialogue"
     5→    data-testid="device-access-dialogue"
     6→    name="DeviceAccessDialogue"
     7→    :closeWhenClickButton="closeOnSelect"
     8→    :title="dialogueTitle"
     9→    :subTitle="subTitle"
    10→    :primaryText="isProcessing ? $t('Selecting') : $t('Select')"
    11→    :buttonIconStatusMapping="buttonIconStatusMapping"
    12→    :state="modalState"
    13→    height="720px"
    14→    width="1116px"
    15→    @confirm="handleConfirm"
    16→  >
    17→    <template #content>
    18→      <div class="content-wrapper">
    19→        <div class="hint">
    20→          <h3 class="bold">
    21→            {{ selectedLengthHint }}
    22→          </h3>
    23→        </div>
    24→        <div class="dialogue-content">
    25→          <div class="left-menu">
    26→            <DeviceGroupList
    27→              :multiple="multiple"
    28→              :groupList="sites"
    29→              :currentSelectedGroups="currentSelectedSites"
    30→              :currentSelectedDevices="currentSelectedDevices"
    31→              :focusGroupId="focusSiteId"
    32→              :isSearching="isSearching"
    33→              :additionalGroupTypes="additionalGroupTypes"
    34→              @toggleSelectedGroups="toggleSelectedSites"
    35→              @changeFocusGroup="changeFocusSiteAndClearInput"
    36→            >
    37→              <template #group-icon="{ group }">
    38→                <SvgIcon
    39→                  :iconClass="customGroupIcon[group.type]"
    40→                  size="24px"
    41→                />
    42→              </template>
    43→            </DeviceGroupList>
    44→          </div>
    45→          <div class="center-view">
    46→            <div class="search-container">
    47→              <AppInputFields
    48→                id="search-device-fields"
    49→                v-model.lazy="searchName"
    50→                class="search-input"
    51→                type="search"
    52→                :disabled="devices.length === 0"
    53→                @change="searchInputChange"
    54→              />
    55→            </div>
    56→            <template v-if="hasSearchResult">
    57→              <div
    58→                v-if="currentFocusSiteInfo"
    59→                class="site-header-container"
    60→              >
    61→                <h6 class="site-hint">
    62→                  {{ currentFocusSiteInfo }}
    63→                </h6>
    64→              </div>
    65→              <div class="site-batch-assign-container">
    66→                <h6 class="site-batch-selector-hint">
    67→                  {{ t('Update Selected Devices Access') }}
    68→                </h6>
    69→                <div class="site-batch-assign-selector">
    70→                  <DeviceRoleDropdown
    71→                    class="device-role-dropdown"
    72→                    :modelValue="currentBatchAssignRoleId"
    73→                    :placeholder="t('Select Role to assign')"
    74→                    :disabled="isBatchAssignDisabled"
    75→                    @selected="batchAssignRole"
    76→                  />
    77→                </div>
    78→              </div>
    79→              <div
    80→                ref="siteList"
    81→                class="site-list"
    82→              >
    83→                <div
    84→                  v-for="(_site, index) in filteredSites"
    85→                  :key="index"
    86→                  class="site-wrapper"
    87→                >
    88→                  <template v-if="displayDevices[_site.id]">
    89→                    <h6
    90→                      class="site-title"
    91→                      :title="_site.name"
    92→                    >
    93→                      {{ _site.name }}
    94→                    </h6>
    95→                    <div class="site-device-container">
    96→                      <template v-if="multiple">
    97→                        <AppCheckBox
    98→                          v-model.lazy="isSelectAllMap[_site.id]"
    99→                          :indeterminate="checkIsSelectIndeterminate(_site.devices)"
   100→                          :disabled="_site.devices.length === 0"
   101→                          @click.prevent="selectAll(_site.id)"
   102→                        />
   103→                      </template>
   104→                      <div class="site-device-subtitle-container">
   105→                        <div class="device-subtitle">
   106→                          {{ t('Devices') }}
   107→                        </div>
   108→                        <div class="device-role-dropdown-subtitle">
   109→                          {{ t('Device Access Role') }}
   110→                        </div>
   111→                      </div>
   112→                    </div>
   113→                  </template>
   114→                  <AppDeviceList
   115→                    class="site-device-list"
   116→                    :multiple="multiple"
   117→                    :selectMode="!multiple"
   118→                    :devicesData="displayDevices[_site.id]"
   119→                    :activeDeviceId="activeDeviceId"
   120→                    :defaultSelectedDevices="currentSelectedDevices"
   121→                    :enableListItemActive="false"
   122→                    :shouldDisplayThumbnail="shouldDisplayThumbnail(_site.type)"
   123→                    @toggleSelectedDevices="toggleSelectedDevices"
   124→                    @selectDevice="selectDevice"
   125→                  >
   126→                    <template #rear="{ device }">
   127→                      <DeviceIndicator
   128→                        v-if="device.nvrName"
   129→                        :device="device"
   130→                      />
   131→                      <DeviceIndicator
   132→                        v-if="device.vssName"
   133→                        :device="device"
   134→                      />
   135→                    </template>
   136→                    <template #dropdown="{ device }">
   137→                      <DeviceRoleDropdown
   138→                        class="device-role-dropdown"
   139→                        :modelValue="deviceRoles[device.deviceId]?.roleId || ''"
   140→                        :placeholder="$t('Select Role')"
   141→                        :initialDisplayText="deviceRoles[device.deviceId]?.roleName || ''"
   142→                        @selected="(role) => updateDeviceRole(device.deviceId, role)"
   143→                      />
   144→                    </template>
   145→                  </AppDeviceList>
   146→                </div>
   147→              </div>
   148→            </template>
   149→            <div
   150→              v-else
   151→              class="no-result-container"
   152→            >
   153→              <SvgIcon
   154→                class="no-result-icon"
   155→                :iconClass="`search_failed`"
   156→                size="100px"
   157→              />
   158→              <h6 class="text">
   159→                {{ $t('No search results') }}
   160→              </h6>
   161→            </div>
   162→          </div>
   163→        </div>
   164→      </div>
   165→    </template>
   166→    <template
   167→      v-if="isError"
   168→      #errorMessage
   169→    >
   170→      <AppFormErrorMessage :message="errorMessage" />
   171→    </template>
   172→  </AppDialogue>
   173→</template>
   174→
   175→<script setup>
   176→import { ref, computed, useTemplateRef, onMounted } from 'vue';
   177→import { useI18n } from 'vue-i18n';
   178→import { useStore } from 'vuex';
   179→
   180→import { DeviceIndicator, DeviceGroupList, AppDeviceList } from '@vivotek/advanced-vue-components';
   181→import { DIALOGUE, GROUP_TYPES, GROUP_ICONS } from '@vivotek/const-vsaas';
   182→
   183→import DeviceRoleDropdown from '@/components/Dropdowns/DeviceRoleDropdown.vue';
   184→import { PERMISSION_DEVICE_SCOPE } from '@/constants/Permission';
   185→import { useDeviceRoleHelper } from '@/pages/user/detail/composables/useDeviceRoleHelper';
   186→
   187→const props = defineProps({
   188→  multiple: {
   189→    type: Boolean,
   190→    default: true,
   191→  },
   192→  availableDevices: {
   193→    type: Array,
   194→    default: () => [],
   195→  },
   196→  closeOnSelect: {
   197→    type: Boolean,
   198→    default: false,
   199→  },
   200→  title: {
   201→    type: String,
   202→    default: '',
   203→  },
   204→  subTitle: {
   205→    type: String,
   206→    default: '',
   207→  },
   208→  userId: {
   209→    type: String,
   210→    default: '',
   211→  },
   212→});
   213→
   214→const emit = defineEmits(['success']);
   215→
   216→const store = useStore();
   217→const { t } = useI18n();
   218→
   219→const modal = useTemplateRef('modal');
   220→const siteList = useTemplateRef('siteList');
   221→
   222→const modalState = ref(DIALOGUE.STATE.DEFAULT);
   223→const searchName = ref('');
   224→const focusSiteId = ref('');
   225→const batchAssignRoleId = ref({});
   226→
   227→const groupState = computed(() => store.state.group);
   228→const getAvailableDevicesWithPermission = computed(() =>
   229→  store.getters['device/getAvailableDevicesWithPermission'](PERMISSION_DEVICE_SCOPE.DEVICE_ALL_USERS),
   230→);
   231→const errorMessage = computed(() => {
   232→  if (isError.value) {
   233→    return t('Failed to assign device roles');
   234→  }
   235→  return '';
   236→});
   237→const customGroupIcon = computed(() => {
   238→  return {
   239→    ...GROUP_ICONS,
   240→    [GROUP_TYPES.SITE]: 'general_site_solid',
   241→    [GROUP_TYPES.NETWORK_SPEAKER]: 'general_speaker_solid',
   242→    [GROUP_TYPES.POE_SWITCH]: 'general_poe_solid',
   243→  };
   244→});
   245→
   246→const devices = computed(() => {
   247→  if (props.availableDevices.length > 0) {
   248→    return props.availableDevices;
   249→  }
   250→  return getAvailableDevicesWithPermission.value || [];
   251→});
   252→
   253→const {
   254→  sites,
   255→  deviceRoles,
   256→  currentSelectedDevices,
   257→  processSelectedDevices,
   258→  processRemovedDevices,
   259→  updateSelectedDevicesWithParentChannel,
   260→  toggleSelectedSites,
   261→  initializeSelectedDevicesAndRoles,
   262→} = useDeviceRoleHelper(devices);
   263→
   264→const additionalGroupTypes = computed(() => [
   265→  {
   266→    type: GROUP_TYPES.NETWORK_SPEAKER,
   267→    label: t('Network Speaker'),
   268→  },
   269→  {
   270→    type: GROUP_TYPES.POE_SWITCH,
   271→    label: t('PoE Switch'),
   272→  },
   273→]);
   274→
   275→const currentSelectedSites = computed(() => {
   276→  return sites.value
   277→    .filter((site) => site.devices.some((device) => currentSelectedDevices.value.includes(device.deviceId)))
   278→    .map((item) => item.id);
   279→});
   280→
   281→const isSelectAllMap = computed(() => {
   282→  return sites.value.reduce(
   283→    (acc, group) => ({
   284→      ...acc,
   285→      [group.id]: group.devices.every((item) => currentSelectedDevices.value.includes(item.deviceId)),
   286→    }),
   287→    {},
   288→  );
   289→});
   290→
   291→const isProcessing = computed(() => {
   292→  return modalState.value === DIALOGUE.STATE.PROCESSING;
   293→});
   294→
   295→const isError = computed(() => {
   296→  return modalState.value === DIALOGUE.STATE.ERROR;
   297→});
   298→
   299→const isInvalidSelection = computed(() => {
   300→  return currentSelectedDevices.value.length === 0;
   301→});
   302→
   303→const isSearching = computed(() => {
   304→  return searchName.value.length > 0;
   305→});
   306→
   307→const activeDeviceId = computed(() => {
   308→  return groupState.value.activeDeviceId;
   309→});
   310→
   311→const dialogueTitle = computed(() => {
   312→  return props.title || t('Select Devices');
   313→});
   314→
   315→const selectedLengthHint = computed(() => {
   316→  const count = currentSelectedDevices.value.length;
   317→  return t('0 device is selected | 1 device is selected | {count} devices are selected', { count });
   318→});
   319→
   320→const filteredSites = computed(() => {
   321→  if (isSearching.value) {
   322→    return sites.value;
   323→  }
   324→
   325→  return sites.value.filter((item) => focusSiteId.value === item.id);
   326→});
   327→
   328→const hasSearchResult = computed(() => {
   329→  return Object.entries(displayDevices.value).length > 0;
   330→});
   331→
   332→const displayDevices = computed(() => {
   333→  const displayResult = {};
   334→
   335→  sites.value.forEach((group) => {
   336→    let result = group.devices;
   337→    if (isSearching.value) {
   338→      result = result.filter((item) => item.displayName.toLowerCase().includes(searchName.value.toLowerCase()));
   339→    }
   340→    if (result.length > 0) {
   341→      displayResult[group.id] = result;
   342→    }
   343→  });
   344→  return displayResult;
   345→});
   346→
   347→const buttonIconStatusMapping = computed(() => {
   348→  return {
   349→    ...DIALOGUE.PRIMARY_ACITON_BUTTON_MAPPING,
   350→    [DIALOGUE.STATE.DEFAULT]: [
   351→      { status: isInvalidSelection.value ? 'disabled' : '', icon: '' },
   352→      { status: '', icon: '' },
   353→    ],
   354→  };
   355→});
   356→
   357→const currentFocusSite = computed(() => {
   358→  if (!focusSiteId.value) {
   359→    return null;
   360→  }
   361→  return sites.value.find((site) => site.id === focusSiteId.value) || null;
   362→});
   363→
   364→const currentFocusSiteInfo = computed(() => {
   365→  if (!currentFocusSite.value) {
   366→    return null;
   367→  }
   368→
   369→  const totalDevices = currentFocusSite.value.devices.length;
   370→  const selectedDevices = currentFocusSite.value.devices.filter((device) =>
   371→    currentSelectedDevices.value.includes(device.deviceId),
   372→  ).length;
   373→
   374→  return `${t('Selected Devices in')} ${currentFocusSite.value.name} (${selectedDevices}/${totalDevices})`;
   375→});
   376→
   377→const currentFocusSiteSelectedDevices = computed(() => {
   378→  if (!currentFocusSite.value || isSearching.value) {
   379→    return [];
   380→  }
   381→  return currentFocusSite.value.devices.filter((device) => currentSelectedDevices.value.includes(device.deviceId));
   382→});
   383→
   384→const currentBatchAssignRoleId = computed({
   385→  get: () => batchAssignRoleId.value[focusSiteId.value] || '',
   386→  set: (value) => {
   387→    if (focusSiteId.value) {
   388→      batchAssignRoleId.value = {
   389→        ...batchAssignRoleId.value,
   390→        [focusSiteId.value]: value,
   391→      };
   392→    }
   393→  },
   394→});
   395→
   396→const isBatchAssignDisabled = computed(() => {
   397→  return currentFocusSiteSelectedDevices.value.length === 0;
   398→});
   399→
   400→const changeFocusSite = (siteId) => {
   401→  focusSiteId.value = siteId;
   402→};
   403→
   404→const setFirstSiteToDefaultFocusSite = () => {
   405→  if (!sites.value.length) {
   406→    return;
   407→  }
   408→  const defaultFocusSiteId = sites.value[0].id;
   409→  changeFocusSite(defaultFocusSiteId);
   410→};
   411→
   412→const clearInput = () => {
   413→  searchName.value = '';
   414→};
   415→
   416→const setDefault = () => {
   417→  modalState.value = DIALOGUE.STATE.DEFAULT;
   418→  batchAssignRoleId.value = {};
   419→  store.commit('user/resetSelectUserDeviceRoles');
   420→  clearInput();
   421→  setFirstSiteToDefaultFocusSite();
   422→  initializeSelectedDevicesAndRoles();
   423→};
   424→
   425→const openDialogue = () => {
   426→  setDefault();
   427→  modal.value?.show();
   428→};
   429→
   430→const selectDevice = (deviceId) => {
   431→  if (props.multiple) {
   432→    return;
   433→  }
   434→  currentSelectedDevices.value = [deviceId];
   435→};
   436→
   437→const changeFocusSiteAndClearInput = (siteId) => {
   438→  clearInput();
   439→  changeFocusSite(siteId);
   440→  if (siteList.value) {
   441→    siteList.value.scrollTop = 0;
   442→  }
   443→};
   444→
   445→const checkIsSelectIndeterminate = (siteDevices) => {
   446→  if (devices.value.length === 0 || !siteDevices || siteDevices.length === 0) {
   447→    return false;
   448→  }
   449→  const devicesId = siteDevices.map((item) => item.deviceId);
   450→  return (
   451→    devicesId.every((item) => currentSelectedDevices.value.includes(item)) === false &&
   452→    devicesId.some((item) => currentSelectedDevices.value.includes(item))
   453→  );
   454→};
   455→
   456→const searchInputChange = (input) => {
   457→  if (!input) {
   458→    setFirstSiteToDefaultFocusSite();
   459→  }
   460→};
   461→
   462→const shouldDisplayThumbnail = (siteType) => {
   463→  return siteType !== GROUP_TYPES.NVR && siteType !== GROUP_TYPES.BRIDGE && siteType !== GROUP_TYPES.VSS;
   464→};
   465→
   466→const updateDeviceRole = (deviceId, role) => {
   467→  deviceRoles.value[deviceId] = {
   468→    roleId: role?.id || null,
   469→    roleName: role?.name || '',
   470→  };
   471→};
   472→
   473→const handleConfirm = async (isConfirm) => {
   474→  if (!isConfirm) {
   475→    return;
   476→  }
   477→
   478→  if (!props.closeOnSelect) {
   479→    modalState.value = DIALOGUE.STATE.PROCESSING;
   480→  }
   481→
   482→  const grantedDevicesMap = new Map();
   483→  const selectedDeviceIdsSet = new Set(currentSelectedDevices.value);
   484→
   485→  processSelectedDevices({ selectedDeviceIdsSet, grantedDevicesMap });
   486→  processRemovedDevices({ selectedDeviceIdsSet, grantedDevicesMap });
   487→
   488→  const grantedDevices = Array.from(grantedDevicesMap.values());
   489→
   490→  try {
   491→    await store.dispatch('user/updateDeviceRoleOfUser', { userId: props.userId, assignments: grantedDevices });
   492→    modalState.value = DIALOGUE.STATE.SUCCESS;
   493→    emit('success');
   494→  } catch (error) {
   495→    modalState.value = DIALOGUE.STATE.ERROR;
   496→  }
   497→};
   498→
   499→const toggleSelectedDevices = ({ selectedDevices }) => {
   500→  currentSelectedDevices.value = updateSelectedDevicesWithParentChannel(selectedDevices, currentSelectedDevices.value);
   501→};
   502→
   503→const selectAll = (groupId) => {
   504→  const devicesId = displayDevices.value[groupId].map((device) => device.deviceId);
   505→  const currentSelected = currentSelectedDevices.value;
   506→  const isAllSelected = devicesId.every((deviceId) => currentSelectedDevices.value.includes(deviceId));
   507→
   508→  if (isAllSelected) {
   509→    const targetSelectedDevices = currentSelected.filter((deviceId) => !devicesId.includes(deviceId));
   510→    currentSelectedDevices.value = updateSelectedDevicesWithParentChannel(targetSelectedDevices, currentSelected);
   511→  } else {
   512→    const targetSelectedDevices = [...new Set([...currentSelected, ...devicesId])];
   513→    currentSelectedDevices.value = updateSelectedDevicesWithParentChannel(targetSelectedDevices, currentSelected);
   514→  }
   515→};
   516→
   517→const batchAssignRole = (role) => {
   518→  if (isBatchAssignDisabled.value) {
   519→    return;
   520→  }
   521→  currentBatchAssignRoleId.value = role?.id || '';
   522→  currentFocusSiteSelectedDevices.value.forEach((device) => {
   523→    deviceRoles.value[device.deviceId] = {
   524→      roleId: role?.id || null,
   525→      roleName: role?.name || '',
   526→    };
   527→  });
   528→};
   529→
   530→defineExpose({
   531→  openDialogue,
   532→});
   533→
   534→onMounted(async () => {
   535→  setFirstSiteToDefaultFocusSite();
   536→  await store.dispatch('user/fetchDefaultDeviceRoleList');
   537→});
   538→</script>
   539→
   540→<style lang="less" scoped>
