   555→      > div {
   556→        padding-left: 40px;
   557→        padding-right: 40px;
   558→      }
   559→    }
   560→  }
   561→
   562→  .site-wrapper {
   563→    display: flex;
   564→    flex-direction: column;
   565→  }
   566→  .search-container {
   567→    display: flex;
