   604→    padding: 18px 0;
   605→  }
   606→  .site-batch-assign-selector {
   607→    width: 30%;
   608→  }
   609→  .dialogue-content {
   610→    flex: 1;
   611→    display: flex;
   612→    flex-direction: row;
