   614→    position: relative;
   615→    min-height: 0;
   616→    padding-top: 10px;
   617→
   618→    .left-menu {
   619→      background-color: @color-surface03;
   620→      border-right: 1px solid #000;
   621→      height: 100%;
   622→    }
   623→    .center-view {
   624→      flex: 1;
   625→      display: flex;
   626→      flex-direction: column;
   627→      margin-left: 20px;
   628→      overflow-x: hidden;
   629→      height: 100%;
   630→    }
   631→  }
   632→  .site-list {
   633→    overflow-y: auto;
   634→    height: 440px;
   635→    margin-bottom: 20px;
   636→    .site-device-list {
   637→      :deep(.device) {
   638→        h6 {
   639→          max-width: 500px;
   640→        }
   641→        .svg-icon {
   642→          &[data-state='active'] {
   643→            background-color: transparent;
   644→          }
   645→        }
   646→      }
   647→    }
   648→  }
   649→  .site-title {
   650→    font-weight: bold;
   651→    padding: 18px;
   652→    font-size: 16px;
   653→    align-items: center;
   654→    line-height: 24px;
   655→    .text-ellipsis;
   656→  }
   657→  .site-device-container {
   658→    display: flex;
   659→    flex-direction: row;
   660→    align-items: center;
   661→    padding: 12px 12px 12px 18px;
   662→    background-color: @color-surface07;
   663→
   664→    :deep(.check-box .svg-icon) {
   665→      margin-right: 10px;
   666→    }
   667→  }
   668→  .site-device-subtitle-container {
   669→    width: 100%;
   670→    display: flex;
