     1→import { ref, computed } from 'vue';
     2→import { useStore } from 'vuex';
     3→
     4→import { GROUP_TYPES, DEVICE_TYPE } from '@vivotek/const-vsaas';
     5→
     6→import {
     7→  composeDeviceId,
     8→  parseDeviceId,
     9→  getParentDeviceId,
    10→  getBridgeDeviceId,
    11→  isChildDevice,
    12→} from '@/utils/DeviceChannelHelper';
    13→
    14→export const DEFAULT_VIEWER_ROLE_ID = 'role-device-viewer';
    15→
    16→export const useDeviceRoleHelper = (devices) => {
    17→  const store = useStore();
    18→
    19→  const deviceRoles = ref({});
    20→  const currentSelectedDevices = ref([]);
    21→  const originalGrantedDevices = ref(new Map());
    22→
    23→  const userDetailGrantedDevices = computed(() => store.getters['user/detailDeviceOverview']);
    24→  const availableExternalDevices = computed(() => store.getters['externalDevice/availableExternalDevices']);
    25→  const allAvailableDevices = computed(() => {
    26→    return [...devices.value, ...availableExternalDevices.value];
    27→  });
    28→  const sortedGroups = computed(() => store.getters['group/sortedGroups']);
    29→
    30→  const sites = computed(() => {
    31→    const groupsValue = sortedGroups.value();
    32→    const normalSites = groupsValue
    33→      .map((group) => ({
    34→        ...group,
    35→        devices: devices.value.filter((device) => {
    36→          return device.options?.deviceGroupID === group.id;
    37→        }),
    38→      }))
    39→      .filter((group) => group.devices.length > 0);
    40→
    41→    const ipSpeakerDevices = availableExternalDevices.value.filter(
    42→      (device) => device.type === DEVICE_TYPE.NETWORK_SPEAKER,
    43→    );
    44→    const ipSpeakerSite =
    45→      ipSpeakerDevices.length > 0
    46→        ? [
    47→            {
    48→              devices: ipSpeakerDevices,
    49→              id: DEVICE_TYPE.NETWORK_SPEAKER,
    50→              index: 999,
    51→              name: 'Network Speaker',
    52→              type: GROUP_TYPES.NETWORK_SPEAKER,
    53→            },
    54→          ]
    55→        : [];
    56→
    57→    const poeSwitchDevices = availableExternalDevices.value.filter((device) => device.type === DEVICE_TYPE.POE_SWITCH);
    58→    const poeSwitchSite =
    59→      poeSwitchDevices.length > 0
    60→        ? [
    61→            {
    62→              devices: poeSwitchDevices,
    63→              id: DEVICE_TYPE.POE_SWITCH,
    64→              index: 998,
    65→              name: 'POE Switch',
    66→              type: GROUP_TYPES.POE_SWITCH,
    67→            },
    68→          ]
    69→        : [];
    70→
    71→    return [...normalSites, ...ipSpeakerSite, ...poeSwitchSite];
    72→  });
    73→
    74→  const deviceMap = computed(() => {
    75→    return new Map(allAvailableDevices.value.map((device) => [device.deviceId, device]));
    76→  });
    77→
    78→  const availableDevicesId = computed(() => {
    79→    return allAvailableDevices.value.map((device) => device.deviceId);
    80→  });
    81→
    82→  const createGrantedDevice = (deviceId, roleId = '') => {
    83→    const { thingName, derivant } = parseDeviceId(deviceId);
    84→
    85→    return {
    86→      thingName,
    87→      derivant,
    88→      roleId,
    89→    };
    90→  };
    91→
    92→  /**
    93→   * handle selected devices
    94→   */
    95→  const processSelectedDevices = ({ selectedDeviceIdsSet, grantedDevicesMap }) => {
    96→    currentSelectedDevices.value.forEach((deviceId) => {
    97→      const roleId = deviceRoles.value[deviceId]?.roleId;
    98→      const finalRoleId = roleId === undefined || roleId === '' ? DEFAULT_VIEWER_ROLE_ID : roleId;
    99→      grantedDevicesMap.set(deviceId, createGrantedDevice(deviceId, finalRoleId));
   100→    });
   101→  };
   102→
   103→  /**
   104→   * handle removed devices
   105→   */
   106→  const processRemovedDevices = ({ selectedDeviceIdsSet, grantedDevicesMap }) => {
   107→    originalGrantedDevices.value.forEach((_, deviceId) => {
   108→      if (selectedDeviceIdsSet.has(deviceId)) {
   109→        return;
   110→      }
   111→      const device = deviceMap.value.get(deviceId);
   112→      grantedDevicesMap.set(deviceId, createGrantedDevice(deviceId, null));
   113→
   114→      removeChildDevices(device, grantedDevicesMap);
   115→    });
   116→  };
   117→
   118→  /**
   119→   * remove child devices (channels/external devices) when their parent/bridge is removed
   120→   */
   121→  const removeChildDevices = (parentDevice, grantedDevicesMap) => {
   122→    originalGrantedDevices.value.forEach((_, childDeviceId) => {
   123→      const childDevice = deviceMap.value.get(childDeviceId);
   124→      if (isChildDevice(childDevice, parentDevice)) {
   125→        grantedDevicesMap.set(childDeviceId, createGrantedDevice(childDeviceId, null));
   126→      }
   127→    });
   128→  };
   129→
   130→  /**
   131→   * find all child devices of parent devices
   132→   */
   133→  const getChannelsOfParents = (parentDeviceIds) => {
   134→    const childDevices = new Set();
   135→
   136→    parentDeviceIds.forEach((parentDeviceId) => {
   137→      const parentDevice = deviceMap.value.get(parentDeviceId);
   138→
   139→      if (!parentDevice) {
   140→        return;
   141→      }
   142→
   143→      allAvailableDevices.value.forEach((device) => {
   144→        if (isChildDevice(device, parentDevice)) {
   145→          childDevices.add(device.deviceId);
   146→        }
   147→      });
   148→    });
   149→
   150→    return childDevices;
   151→  };
   152→
   153→  /**
   154→   * find all parents (NVR/VSS) and bridge devices to add
   155→   */
   156→  const getParentsToAdd = (deviceIds, currentSelectedSet) => {
   157→    const parents = new Set();
   158→
   159→    deviceIds.forEach((deviceId) => {
   160→      const device = deviceMap.value.get(deviceId);
   161→
   162→      // Handle NVR/VSS channel parent devices
   163→      const parentDeviceId = getParentDeviceId(device);
   164→      if (parentDeviceId && !currentSelectedSet.has(parentDeviceId)) {
   165→        parents.add(parentDeviceId);
   166→      }
   167→
   168→      // Handle external device bridge devices
   169→      const bridgeDeviceId = getBridgeDeviceId(device);
   170→      if (bridgeDeviceId && !currentSelectedSet.has(bridgeDeviceId)) {
   171→        parents.add(bridgeDeviceId);
   172→      }
   173→    });
   174→
   175→    return Array.from(parents);
   176→  };
   177→
   178→  const updateSelectedDevicesWithParentChannel = (targetSelectedDevices, currentSelectDevices) => {
   179→    const targetSet = new Set(targetSelectedDevices);
   180→    const currentSet = new Set(currentSelectDevices);
   181→
   182→    const deselectedDevices = currentSelectDevices.filter((id) => !targetSet.has(id));
   183→    const newSelectedDevices = targetSelectedDevices.filter((id) => !currentSet.has(id));
   184→
   185→    // handle deselected devices: check if deselected devices are parents/bridges, and find child devices to remove
   186→    const channelsToRemove = getChannelsOfParents(deselectedDevices);
   187→    const finalSelectedDevices = targetSelectedDevices.filter((id) => !channelsToRemove.has(id));
   188→
   189→    // handle selected devices: check if new selected devices need parent/bridge devices, and add them
   190→    const finalSelectedSet = new Set(finalSelectedDevices);
   191→    const parentsToAdd = getParentsToAdd(newSelectedDevices, finalSelectedSet);
   192→
   193→    // set roles for auto-added parents if they don't have one yet
   194→    parentsToAdd.forEach((parentId) => {
   195→      if (!deviceRoles.value[parentId]) {
   196→        const originalDevice = originalGrantedDevices.value.get(parentId);
   197→        deviceRoles.value[parentId] = {
   198→          roleId: originalDevice?.roleId || DEFAULT_VIEWER_ROLE_ID,
   199→          roleName: originalDevice?.roleName || '',
   200→        };
   201→      }
   202→    });
   203→
   204→    return [...finalSelectedDevices, ...parentsToAdd];
   205→  };
   206→
   207→  const getSiteDevicesId = (siteIds) => {
   208→    return sites.value
   209→      .filter((site) => siteIds.includes(site.id))
   210→      .flatMap((site) => site.devices.map((device) => device.deviceId))
   211→      .filter((deviceId) => availableDevicesId.value.indexOf(deviceId) >= 0);
   212→  };
   213→
   214→  const getIndeterminateGroupDevices = (indeterminateSites) => {
   215→    const indeterminateSiteDevicesId = getSiteDevicesId(indeterminateSites);
   216→    return currentSelectedDevices.value.filter((deviceId) => indeterminateSiteDevicesId.includes(deviceId));
   217→  };
   218→
   219→  const toggleSelectedSites = (selectedSites, indeterminateSites = []) => {
   220→    const indeterminateSiteDevices = getIndeterminateGroupDevices(indeterminateSites);
   221→    const fullySelectedSites = selectedSites.filter((group) => !indeterminateSites.includes(group));
   222→    const fullySelectedDevices = getSiteDevicesId(fullySelectedSites);
   223→    const targetSelectedDevices = [...fullySelectedDevices, ...indeterminateSiteDevices];
   224→
   225→    currentSelectedDevices.value = updateSelectedDevicesWithParentChannel(
   226→      targetSelectedDevices,
   227→      currentSelectedDevices.value,
   228→    );
   229→  };
   230→
   231→  /**
   232→   * initialize selected devices and roles
   233→   * only select devices that exist in availableDevicesId
   234→   */
   235→  const initializeSelectedDevicesAndRoles = () => {
   236→    if (!userDetailGrantedDevices.value || userDetailGrantedDevices.value.length === 0) {
   237→      return;
   238→    }
   239→    const selectedDeviceIds = [];
   240→    const roleMapping = {};
   241→    const availableDeviceIdsSet = new Set(availableDevicesId.value);
   242→
   243→    originalGrantedDevices.value.clear();
   244→    userDetailGrantedDevices.value.forEach((grantedDevice) => {
   245→      const deviceId = composeDeviceId(grantedDevice.thingName, grantedDevice.derivant);
   246→      originalGrantedDevices.value.set(deviceId, { ...grantedDevice });
   247→
   248→      // only select devices that exist in availableDevicesId
   249→      if (availableDeviceIdsSet.has(deviceId)) {
   250→        selectedDeviceIds.push(deviceId);
   251→        roleMapping[deviceId] = {
   252→          roleId: grantedDevice.roleId || '',
   253→          roleName: grantedDevice.roleName || '',
   254→        };
   255→      }
   256→    });
   257→
   258→    currentSelectedDevices.value = selectedDeviceIds;
   259→    deviceRoles.value = roleMapping;
   260→  };
   261→
   262→  return {
   263→    sites,
   264→    deviceRoles,
   265→    currentSelectedDevices,
   266→    originalGrantedDevices,
   267→    processSelectedDevices,
   268→    processRemovedDevices,
   269→    updateSelectedDevicesWithParentChannel,
   270→    toggleSelectedSites,
   271→    initializeSelectedDevicesAndRoles,
   272→  };
   273→};
