     1→import { ref, computed, watch } from 'vue';
     2→import { useI18n } from 'vue-i18n';
     3→import { useStore } from 'vuex';
     4→
     5→const VIRTUAL_SITE_ID = {
     6→  IPSPEAKER: 'virtual-ipspeaker',
     7→  POESWITCH: 'virtual-poeswitch',
     8→};
     9→
    10→const VIRTUAL_SITE_ICON = {
    11→  [VIRTUAL_SITE_ID.IPSPEAKER]: 'general_speaker_solid',
    12→  [VIRTUAL_SITE_ID.POESWITCH]: 'general_poe_solid',
    13→};
    14→
    15→/**
    16→ * Device Access related logic
    17→ * @param {import('vue').Ref} currentUser - The user being viewed
    18→ */
    19→export default function useDeviceAccessHelper(currentUser) {
    20→  const store = useStore();
    21→  const { t } = useI18n();
    22→
    23→  const selectedSites = ref([]);
    24→
    25→  const deviceOverview = computed(() => store.getters['user/detailDeviceOverview']);
    26→  const isLoading = computed(() => store.getters['user/detailIsLoading']);
    27→  const myDevices = computed(() => [
    28→    ...(store.getters['device/availableDevices'] || []),
    29→    ...(store.getters['externalDevice/availableExternalDevices'] || []),
    30→  ]);
    31→  const mySites = computed(() => store.getters['group/groupsList'] || []);
    32→  const currentOrgId = computed(() => store.getters['organization/currentOrganization']?.organizationID);
    33→
    34→  const findMyDeviceInfo = (thingName, derivant) => {
    35→    return myDevices.value.find(
    36→      (device) => device.thingName === thingName && (device.options?.derivant === derivant || derivant === 'none'),
    37→    );
    38→  };
    39→
    40→  const findSiteName = (siteId) => {
    41→    const group = mySites.value.find((g) => g.id === siteId);
    42→    return group?.name || '-';
    43→  };
    44→
    45→  const getVirtualSiteId = (type) => {
    46→    if (type === 'ipspeaker') {
    47→      return VIRTUAL_SITE_ID.IPSPEAKER;
    48→    }
    49→    if (type === 'poeSwitch') {
    50→      return VIRTUAL_SITE_ID.POESWITCH;
    51→    }
    52→    return null;
    53→  };
    54→
    55→  const getVirtualSiteName = (virtualSiteId) => {
    56→    if (virtualSiteId === VIRTUAL_SITE_ID.IPSPEAKER) {
    57→      return t('Network Speaker');
    58→    }
    59→    if (virtualSiteId === VIRTUAL_SITE_ID.POESWITCH) {
    60→      return t('PoE Switch');
    61→    }
    62→    return '-';
    63→  };
    64→
    65→  /**
    66→   * Transform API raw data (grantedDevices) into sites structure
    67→   */
    68→  const sites = computed(() => {
    69→    const grantedDevices = deviceOverview.value || [];
    70→    if (grantedDevices.length === 0) {
    71→      return [];
    72→    }
    73→
    74→    const siteMap = new Map();
    75→
    76→    grantedDevices.forEach((grantedDevice) => {
    77→      const { thingName, derivant, siteId, roleId, roleName } = grantedDevice;
    78→
    79→      const myDeviceInfo = findMyDeviceInfo(thingName, derivant);
    80→      const deviceType = myDeviceInfo?.type || 'camera';
    81→
    82→      // ipspeaker and poeswitch go to virtual sites
    83→      const virtualSiteId = getVirtualSiteId(deviceType);
    84→      const targetSiteId = virtualSiteId || siteId;
    85→
    86→      if (!siteMap.has(targetSiteId)) {
    87→        siteMap.set(targetSiteId, {
    88→          id: targetSiteId,
    89→          name: virtualSiteId ? getVirtualSiteName(virtualSiteId) : findSiteName(siteId),
    90→          icon: virtualSiteId ? VIRTUAL_SITE_ICON[virtualSiteId] : null,
    91→          isUngrouped: !virtualSiteId && siteId === currentOrgId.value,
    92→          devices: [],
    93→        });
    94→      }
    95→
    96→      siteMap.get(targetSiteId).devices.push({
    97→        id: `${thingName}-${derivant}`,
    98→        thingName,
    99→        derivant,
   100→        name: myDeviceInfo?.displayName || thingName,
   101→        type: deviceType,
   102→        role: roleName,
   103→        roleId,
   104→      });
   105→    });
   106→
   107→    return Array.from(siteMap.values());
   108→  });
   109→
   110→  watch(
   111→    [selectedSites, sites],
   112→    ([newSelectedSites, newSites]) => {
   113→      if (newSelectedSites.length === 0 && newSites.length > 0) {
   114→        selectedSites.value = newSites.map((site) => site.id);
   115→      }
   116→    },
   117→    { immediate: true },
   118→  );
   119→
   120→  const totalAccess = computed(() => {
   121→    return sites.value.reduce((total, site) => total + site.devices.length, 0);
   122→  });
   123→
   124→  const isOverviewEmpty = computed(() => totalAccess.value === 0);
   125→
   126→  const tableSearchStatus = computed(() => {
   127→    if (isLoading.value) {
   128→      return 'loading';
   129→    }
   130→    if (isOverviewEmpty.value) {
   131→      return 'no-device-group';
   132→    }
   133→    return '';
   134→  });
   135→
   136→  const siteOptions = computed(() => {
   137→    return sites.value.map((site) => ({
   138→      value: site.id,
   139→      text: site.name,
   140→    }));
   141→  });
   142→
   143→  const filteredSites = computed(() => {
   144→    if (selectedSites.value.length === 0) {
   145→      return sites.value;
   146→    }
   147→    return sites.value.filter((site) => selectedSites.value.includes(site.id));
   148→  });
   149→
   150→  const filterTitle = computed(() => {
   151→    if (selectedSites.value.length === 0 || selectedSites.value.length === sites.value.length) {
   152→      return t('All');
   153→    }
   154→    return selectedSites.value
   155→      .map((id) => sites.value.find((site) => site.id === id)?.name)
   156→      .filter(Boolean)
   157→      .join(', ');
   158→  });
   159→
   160→  const fetchDeviceAccess = async () => {
   161→    if (!currentUser.value?.userSub) {
   162→      return;
   163→    }
   164→    await store.dispatch('user/fetchUserGrantedDevices', { userSub: currentUser.value.userSub });
   165→  };
   166→
   167→  return {
   168→    selectedSites,
   169→    sites,
   170→    totalAccess,
   171→    isLoading,
   172→    isOverviewEmpty,
   173→    tableSearchStatus,
   174→    siteOptions,
   175→    filteredSites,
   176→    filterTitle,
   177→    fetchDeviceAccess,
   178→  };
   179→}
