import vue from '@vitejs/plugin-vue';
import { execSync } from 'child_process';
import { resolve } from 'path';
import { defineConfig, loadEnv } from 'vite';

import federation from '@originjs/vite-plugin-federation';
import dnsProxy from '@vivotek/vite-dns-proxy-plugin';
import { visualizer } from 'rollup-plugin-visualizer';
// import { compression, defineAlgorithm } from 'vite-plugin-compression2';
import vitePluginGraphqlLoader from 'vite-plugin-graphql-loader';
import { nodePolyfills } from 'vite-plugin-node-polyfills';
import { VitePWA } from 'vite-plugin-pwa';
import { createSvgIconsPlugin } from 'vite-plugin-svg-icons';
import VueDevTools from 'vite-plugin-vue-devtools';

import loadVendorConfig from './vendorResolver';

const GIT_INFO = {
  VERSION: process.env.npm_package_version,
  COMMIT_DATE: execSync('git log -1 --format=%cI').toString().trimEnd(),
  BRANCH_NAME: execSync('git rev-parse --abbrev-ref HEAD').toString().trimEnd(),
  COMMIT_HASH: execSync('git rev-parse HEAD').toString().trimEnd(),
  TAG: execSync('git describe --tags --abbrev=0 --always').toString().trimEnd(),
};

function decideRemote(env) {
  let REMOTE_AI_PATH = '';
  if (process.env.TEST === 'true') {
    REMOTE_AI_PATH = resolve(__dirname, '../app-vsaas-ai/dist/assets/remoteEntry.js');
  } else {
    REMOTE_AI_PATH = `${env.VITE_MFE_REMOTE_AI_ORIGIN}/assets/remoteEntry.js`;
  }
  return {
    REMOTE_AI_PATH,
  };
}

function decideDnsProxyDomain(mode) {
  const dnsProxyModes = {
    'dns.dev': ['portal.dev.vortexcloud.com'],
    'dns.stage': ['portal.stage.vortexcloud.com'],
    'dns.prod': ['portal.vortexcloud.com'],
  };
  return dnsProxyModes[mode] || null;
}

const generateSharedList = () => {
  const sharedModules = [
    '@vivotek/lib-aws-amplify',
    '@vivotek/lib-hls-player',
    '@vivotek/lib-http-over-channel',
    '@vivotek/lib-rtsp-player',
    '@vivotek/lib-svg-icon',
    '@vivotek/lib-utility',
    '@vivotek/lib-vsaas-storage',
    '@vivotek/lib-vue-pixi-timeline',
    '@vivotek/lib-webrtc-odyssey',
    '@vivotek/theme-color',
    '@vivotek/vue-theme-observer',
    '@vivotek/vue-user-behavior-tracker',
    '@vivotek/context',
    '@vivotek/threadable-canvas',
    '@vivotek/ssmux',
    'vue',
    'vuex',
    'vue-router',
    '@vivotek/advanced-vue-components',
    '@vivotek/const-vsaas',
    '@vivotek/device',
    '@vivotek/vsaas-vue-components',
    '@vivotek/generic-vue-components',
    // Note: @vivotek/shadcn-vue-components and @vivotek/design-tokens are not included
    // in shared modules to avoid package.json export issues with federation plugin
  ];

  return sharedModules.reduce((acc, packageName) => {
    acc[packageName] = { modulePreload: true };
    return acc;
  }, {});
};

export default defineConfig(async ({ mode }) => {
  const env = loadEnv(mode, process.cwd());

  console.log('env:', env, mode);
  console.log('generated source map:', env.VITE_SOURCE_MAP === 'true');

  const mfeConfig = decideRemote(env);
  const vendor = await loadVendorConfig(env.VITE_VENDOR_NAME);

  return {
    plugins: [
      vue(),
      VueDevTools(),
      vitePluginGraphqlLoader(),
      visualizer({
        open: false,
        filename: 'bundle-report.html',
      }),
      createSvgIconsPlugin({
        iconDirs: [
          resolve(process.cwd(), 'src/assets'),
          resolve(process.cwd(), '../lib-svg-icon/svg'),
          resolve(process.cwd(), '../lib-svg-icon-reskin/svg'),
        ],
        symbolId: 'icon-[name]',
        customDomId: '__svg__icons__dom__',
      }),
      VitePWA({
        registerType: 'autoUpdate',
        strategies: 'injectManifest',
        srcDir: 'src',
        filename: 'sw.js',
        workbox: {
          cleanupOutdatedCaches: true,
          sourcemap: true,
        },
        injectManifest: {
          globPatterns: ['**/*.{js,css,html}'],
          maximumFileSizeToCacheInBytes: 7 * 1024 * 1024,
        },
      }),
      nodePolyfills({
        globals: {
          Buffer: true,
          global: true,
          process: true,
        },
      }),
      !process.env.VITEST &&
        federation({
          name: 'host-app',
          filename: 'remoteEntry.js',
          remotes: {
            vortexai: mfeConfig.REMOTE_AI_PATH,
          },
          shared: generateSharedList(),
        }),
      // compression({
      //   algorithms: ['gzip'],
      // }),
      ...(vendor?.prebuild
        ? [
            {
              name: 'prebuild',
              async buildStart() {
                await vendor.prebuild();
              },
            },
          ]
        : []),
      decideDnsProxyDomain(mode) &&
        dnsProxy({
          domains: decideDnsProxyDomain(mode),
        }),
    ],
    define: {
      GIT_INFO,
    },
    resolve: {
      extensions: ['.mjs', '.js', '.ts', '.jsx', '.tsx', '.json', '.vue'],
      alias: {
        '@': resolve(__dirname, './src'),
        '@testVortexai': resolve(__dirname, '../app-vsaas-ai/src'),
        './runtimeConfig': './runtimeConfig.browser',
      },
    },
    server: {
      port: 8080,
    },
    css: {
      preprocessorOptions: {
        less: {
          additionalData: `
              @import '@vivotek/theme-color/vsaas.less';\n
              @import '@vivotek/less-vsaas-common/mediaQueryVariable.less';\n
              @import '@vivotek/less-vsaas-common/mixins.less';\n
            `,
        },
      },
    },
    build: {
      target: 'esnext',
      cssCodeSplit: false,
      reportCompressedSize: false,
      minify: 'esbuild',
      sourcemap: false,
      commonjsOptions: {
        requireReturnsDefault: 'preferred',
      },
      rollupOptions: {
        external: ['src/i18n/languages/*.json', 'src/assets/*.png', 'src/assets/*.svg'],
        input: {
          index: resolve(__dirname, 'index.html'),
          sso: resolve(__dirname, 'sso.html'),
        },
        output: {
          minifyInternalExports: false,
          // eslint-disable-next-line consistent-return
        },
      },
    },
    test: {
      dir: 'src',
      alias: {
        'vortexai/VCARuleSettingsPanel': resolve(__dirname, './mock/MockRemoteComponent.vue'),
        'vortexai/FaceProfileDialogue': resolve(__dirname, './mock/MockRemoteComponent.vue'),
        'vortexai/FaceAlignService': resolve(__dirname, './mock/MockService.js'),
        'vortexai/NoFaceDetectedDialogue': resolve(__dirname, './mock/MockRemoteComponent.vue'),
        'vortexai/MultiFacesDetectedDialogue': resolve(__dirname, './mock/MockRemoteComponent.vue'),
        'vortexai/InsufficientResolutionDialogue': resolve(__dirname, './mock/MockRemoteComponent.vue'),
        'vortexai/FaceCenterWithMarginDialogue': resolve(__dirname, './mock/MockRemoteComponent.vue'),
        'vortexai/VSaasAIRoutes': resolve(__dirname, './mock/MockVSaasAIRoute.js'),
        'vortexai/ExcludedAreaPanel': resolve(__dirname, './mock/MockRemoteComponent.vue'),
      },
      env,
      setupFiles: resolve(__dirname, './vitest.setup.js'),
      css: false,
      globals: true,
      environment: 'jsdom',
      coverage: {
        enabled: true,
      },
      // eslint-disable-next-line consistent-return
      onConsoleLog(log) {
        if (log.includes('Download the Vue Devtools extension')) {
          return false;
        }
        if (log.includes('Vue warn')) {
          return false;
        }
        if (log.includes('You are running Vue in development mode')) {
          return false;
        }
      },
      pool: 'threads',
      maxConcurrency: 20,
      poolOptions: {
        threads: {
          useAtomics: true,
        },
      },
    },
  };
});


