import AlarmRoute from '../pages/alarm/route';
import ArchiveRoute from '../pages/archive/route';
import DeviceRoute from '../pages/device/route';
import FloorplanRoute, { SitesFloorplanRoute } from '../pages/floorplan/route';
import LoginRoute from '../pages/login/route';
import NotificationRoute from '../pages/notification/route';
import NVRForgotPasswordRoute from '../pages/NVRForgotPassword/route';
import RoleRoute from '../pages/role/route';
import SystemRoute from '../pages/system/route';
import TimeLapseRoute from '../pages/Timelapse/route';
import UserRoute from '../pages/user/route';
import ViewRoute from '../pages/view/route';

const staticRoutes = [
  {
    path: '',
    redirect: { name: 'Group' },
  },
  {
    path: '/system',
    component: reskinRouterHandler(
      () => import('../pages/system/index.vue'),
      () => import('../pages/system/reskin/index.vue'),
    ),
    meta: { permission: PERMISSION_KEYS.SYSTEM, freePlanCanAccess: true },
    children: SystemRoute,
  },
  {
    path: '/no-privileges',
    name: 'NoPrivileges',
    component: () => import('../pages/noPrivileges/index.vue'),
    meta: {},
  },
  {
    path: '/group',
    name: 'Group',
    component: () => import('../pages/group/index.vue'),
    meta: { permission: PERMISSION_KEYS.GROUP, licenseForbiddenPage: true },
  },
  {
    path: '/view',
    name: 'View',
    component: () => import('../pages/view/index.vue'),
    redirect: { name: 'ListView' },
    children: ViewRoute,
    meta: { licenseForbiddenPage: true },
  },
  {
    path: '/account',
    name: 'Account',
    component: () => import('../pages/account/index.vue'),
    children: AccountRoute,
    meta: {},
  },
  {
    path: '/login',
    component: () => import('../pages/login/index.vue'),
    meta: {
      layout: 'LoginLayout',
    },
    children: LoginRoute,
  },
  {
    path: '/logout',
    name: 'Logout',
    redirect: { name: 'Login' },
    meta: {
      forceRedirect: true,
    },
  },
  {
    path: '/auth/:idp',
    name: 'AuthIdp',
    component: () => import('../pages/auth/index.vue'),
