  },
  {
    path: '/timelapse/',
    name: 'TimeLapse',
    component: () => import('../pages/Timelapse/index.vue'),
    children: TimeLapseRoute,
    meta: { permission: PERMISSION_KEYS.SYSTEM, authStrategy: AUTH_STRATEGY_TYPES.SESSION_AUTH },
  },
  {
    path: '/role/',
    name: 'Role',
    redirect: { name: 'OrganizationRoleManagement' },
