    children: RoleRoute,
    meta: { permission: PERMISSION_KEYS.ROLE, activeNav: 'user' },
  },
  ...(PAGE_LIST.includes('SEARCH_LIGHT')
    ? [
        {
          path: '/search-light',
          name: 'SearchLight',
          component: () => import('../pages/searchlight/index.vue'),
          meta: { permission: '' },
        },
      ]
    : []),
  {
    path: '/alarm',
    name: 'Alarm',
    redirect: { name: 'AlarmMock' },
    component: () => import('../pages/alarm/index.vue'),
    children: AlarmRoute,
    meta: {},
  },
  // Catch-all route - MUST be last to avoid matching before specific routes
  {
    path: '/:pathMatch(.*)*',
    redirect: { name: 'Group' },
  },
];

const routes = createRouter({
  history: createWebHistory(),
  routes: staticRoutes,
});

export default routes;


