import i18n, { loadLocaleMessages } from '@/i18n';
import languageHelper from '@/i18n/languageHelper';
import store from '@/store';

import RouterMiddleWare from './RouterMiddleware';
import MicrofrontendManager from '../MicrofrontendManager';
import Router from './router';

const router = Router;

function reformatUrl(url) {
  const [baseWithSearch, hash] = url.split('#');
  const search = baseWithSearch.split('?')[1];

  let newUrl;
  if (hash) {
    const [path, query] = hash.split('?');
    newUrl = `${path}${search ? `?${search}` : ''}${query ? `?${query}` : ''}`;
  } else {
    newUrl = url;
  }
  return newUrl;
}

const beforeEach = (to, from, next) => {
  if (!from.redirectedFrom && to.hash) {
    const $_from = to.redirectedFrom;
    const oldUrl = !$_from.redirectedFrom ? $_from.hash : $_from.redirectedFrom;
    const newUrl = reformatUrl(oldUrl);

    next(newUrl);
  }

  if (!from.name) {
    store.commit('ui/setRouterLoading', true);
  }

  if (to.path === '/login') {
    loadLocaleMessages(i18n, languageHelper.getNavigatorLanguage());
  }

  if (
    to?.redirectedFrom &&
    (to?.redirectedFrom.path.includes('/search') ||
      to?.redirectedFrom.path.includes('/profile-search') ||
      to?.redirectedFrom.path.includes('/detection/vca-detection')) &&
    !MicrofrontendManager.loaded
  ) {
    const toPath = to?.redirectedFrom.path;
    MicrofrontendManager.load(store, router).then(() => {
      router.replace({ path: toPath }, () => {
        next();
      });
    });
    return;
  }

  next();
};

const afterEach = (to, from, next) => {
  store.commit('ui/setRouterLoading', false);

  // Toggle v1-style class based on route meta
  // shadcn-ui pages need v1-style removed to avoid CSS conflicts
  if (to.meta?.shadcnUI) {
    document.body.classList.remove('v1-style');
  } else if (from.meta?.shadcnUI) {
    // Restore v1-style when leaving shadcn page
    document.body.classList.add('v1-style');
  }
};

const callNext = (options) => {
  return options;
};

const beforeResolve = async (to, from) => {
  if (to.fullPath === from.fullPath) {
    return false;
  }

  const path = await RouterMiddleWare.decideRoute(to, from);
  return path;
};

router.beforeEach(beforeEach);

router.afterEach(afterEach);

router.beforeResolve(beforeResolve);

export { beforeEach, afterEach, beforeResolve, callNext };

export default router;


