    meta: { permission: PERMISSION_KEYS.SSO, freePlanCanAccess: true },
  },
  {
    path: 'organization-security',
    name: 'OrganizationSecurity',
    component: () => import('./components/OrganizationSecurity.vue'),
    meta: { permission: PERMISSION_KEYS.MFA },
  },
  {
    path: 'global-device-settings',
    meta: { permission: PERMISSION_KEYS.GLOBAL_DEVICE_SETTINGS, licenseForbiddenPage: true, freePlanCanAccess: true },
    children: [
      {
        path: 'auto-firmware-update',
        name: 'AutoFirmwareUpdate',
        component: reskinRouterHandler(
          () => import('./components/AutoFirmwareUpdate.vue'),
          () => import('./reskin/globalDeviceSettings/AutoFirmwareUpdate.vue'),
        ),
        meta: { permission: PERMISSION_KEYS.GLOBAL_DEVICE_SETTINGS, freePlanCanAccess: true },
      },
      {
        path: 'event-delay',
