          () => import('./reskin/AIConsentSettings/index.vue'),
        ),
        meta: { permission: PERMISSION_KEYS.GLOBAL_DEVICE_SETTINGS },
      },
    ],
  },
  {
    // [Note] Redirect for PDK marketplace compatibility
    path: 'access-control',
    redirect: { name: 'AccessControl' },
  },
  {
    path: 'third-party-integration',
    name: 'ThirdPartyIntegration',
    meta: { permission: PERMISSION_KEYS.THIRD_PARTY_INTEGRATION, freePlanCanAccess: true },
