import CreateAccount from './components/CreateAccount.vue';
import ForgotPassword from './components/ForgotPassword.vue';
import NoAssociatedOrganization from './components/NoAssociatedOrganization.vue';
import ResendEmail from './components/ResendEmail.vue';
import SetNewPassword from './components/SetNewPassword.vue';
import SignIn from './components/SignIn.vue';
import SignInSSO from './components/SignInSSO.vue';
import UserAgreement from './components/UserAgreement.vue';
import VerifyMFA from './components/VerifyMFA.vue';

const LoginRoute = [
  {
    path: '',
    name: 'Login',
    component: SignIn,
    meta: {
      layout: 'LoginLayout',
      authStrategy: AUTH_STRATEGY_TYPES.UNAUTH_ONLY,
    },
  },
  {
    path: '/create-account',
    name: 'CreateAccount',
    component: CreateAccount,
    meta: {
      layout: 'LoginLayout',
      authStrategy: AUTH_STRATEGY_TYPES.UNAUTH_ONLY,
    },
  },
  {
    path: '/resend-email',
    name: 'ResendEmail',
    component: ResendEmail,
    meta: {
      layout: 'LoginLayout',
      authStrategy: AUTH_STRATEGY_TYPES.UNAUTH_ONLY,
    },
  },
  {
    path: '/forgot-password',
    name: 'ForgotPassword',
    component: ForgotPassword,
    meta: {
      layout: 'LoginLayout',
      authStrategy: AUTH_STRATEGY_TYPES.UNAUTH_ONLY,
    },
  },
  {
    path: '/set-new-password',
    name: 'SetNewPassword',
    component: SetNewPassword,
    meta: {
      layout: 'LoginLayout',
      authStrategy: AUTH_STRATEGY_TYPES.STEP_AUTH,
    },
  },
  {
    path: '/activation-required',
    name: 'ActivationRequired',
    component: AccountCertificationRequired,
    props: {
      accountCertificationType: 'activation',
    },
    meta: {
      layout: 'LoginLayout',
      authStrategy: AUTH_STRATEGY_TYPES.ANY_ACCESS,
    },
  },
  {
    path: '/invitation-required',
    name: 'InvitationRequired',
    component: AccountCertificationRequired,
    props: {
      accountCertificationType: 'invitation',
    },
    meta: {
      layout: 'LoginLayout',
      authStrategy: AUTH_STRATEGY_TYPES.ANY_ACCESS,
    },
  },
  {
    path: '/new-to-portal',
    name: 'NoAssociatedOrganization',
    component: NoAssociatedOrganization,
    meta: {
      layout: 'LoginLayout',
    },
  },
  {
    path: '/login/:token',
    name: 'RemoteAccess',
    component: SignIn,
    meta: {
      layout: 'LoginLayout',
      authStrategy: AUTH_STRATEGY_TYPES.URL_TOKEN_AUTH,
    },
  },
  {
    path: '/login-sso',
    name: 'LoginSSO',
    component: SignInSSO,
    meta: {
      layout: 'LoginLayout',
      authStrategy: AUTH_STRATEGY_TYPES.UNAUTH_ONLY,
    },
  },
  {
    path: '/setup-mfa',
    name: 'SetupMFA',
    component: SignIn,
    meta: {
      layout: 'LoginLayout',
    },
  },
  {
    path: '/verify-mfa',
    name: 'VerifyMFA',
    component: VerifyMFA,
    meta: {
      layout: 'LoginLayout',
      authStrategy: AUTH_STRATEGY_TYPES.STEP_AUTH,
    },
  },
  {
    path: '/user-agreement',
    name: 'UserAgreement',
    component: UserAgreement,
    meta: {
      layout: 'LoginLayout',
    },
  },
];

export default LoginRoute;


