class MicrofrontendManager {
  constructor() {
    this.loaded = false;
    this.loading = false;
    global.__MFE__ = {
      ENV: import.meta.env,
    };
  }

  // eslint-disable-next-line consistent-return
  async load(store, router) {
    if (this.loaded) {
      return Promise.resolve();
    }
    this.loading = true;
    const [search, profileSearch, caseVault, vcaSettings, eventSearch, info] = await Promise.all([
      import('vortexai/SearchModule'),
      import('vortexai/ProfileSearchModule'),
      import('vortexai/CaseVaultModule'),
      import('vortexai/VCASettingsModule'),
      import('vortexai/EventSearchModule'),
      import('vortexai/AppVersion'),
    ]);

    loadModule(search.default, store, router);
    loadModule(profileSearch.default, store, router);
    loadModule(caseVault.default, store);
    loadModule(vcaSettings.default, store);
    loadModule(eventSearch.default, store);

    addVortexAIRoutesToDevice(router);

    this.aiVersion = info.default;
    addHeaderMeta('ai', this.aiVersion);
    this.loading = false;
    this.loaded = true;
  }
}

function loadModule(module, store, router) {
  if (store.hasModule(module.name)) {
    console.warn('module registered:', module.name);
    return;
  }
  store.registerModule(module.name, module.module);
  if (router) {
    router.addRoute(module.route);
  }
}

const singleton = new MicrofrontendManager();

export default singleton;


