function decideRemote(env) {
  let REMOTE_AI_PATH = '';
  if (process.env.TEST === 'true') {
    REMOTE_AI_PATH = resolve(__dirname, '../app-vsaas-ai/dist/assets/remoteEntry.js');
  } else {
