{"result":"import { createStore } from 'vuex';

import { ERROR_MESSAGES, ERROR_EXTRA_TYPE_CODES } from '@vivotek/const-vsaas';
import Context from '@vivotek/context';

import AuthenticationService from '@/models/Authentication/AuthenticationService';
import ApiServiceInterface from '@/singletons/ApiServiceInterface';
import JWT from '@/utils/JWT';

import accessControl from './accessControl';
import account from './account';
import alarm from './alarm';
import archive from './archive';
import auditLog from './auditLog';
import device from './device';
import externalDevice from './externalDevice';
import floorPlan from './floorPlan';
import globalDialog from './globalDialog';
import globalNotification from './globalNotification';
import group from './group';
import license from './license';
import mfa from './mfa';
import notification from './notification';
import organization from './organization';
import permission from './permission';
import role from './role';
import si from './si';
import singleSignOn from './singleSignOn';
import smartSensor from './smartSensor';
import subscription from './subscription';
import thirdParty from './thirdParty';
import ui from './ui';
import user from './user';
import view from './view';

const store = createStore({
  state: {
    // The reason to put apiService here is because we need to control the error callback flow generally.
    // To response when any error occurs, we pass the commit function to the interface.
    apiService: Context.inject(ApiServiceInterface, {
      ApiErrorProcessor: async (error) => {
        const message = error?.message?.toLowerCase();

        switch (message) {
          case ERROR_MESSAGES.NotFound.toLowerCase():
          case ERROR_MESSAGES.AccessDenied.toLowerCase():
            await store.dispatch('ui/setIsRequestFailed', true);
            break;
          case ERROR_MESSAGES.OutsideOrganization.toLowerCase():
            await store.dispatch('ui/setIsAuthFailed', true);
            break;
          default:
            break;
        }
      },
      ApiErrorProcessorRestful: async (error) => {
        // Check for JWT token expiration first before any other authentication error checks
        try {
          const { jwtToken } = AuthenticationService;
          if (jwtToken && JWT.isJwtTokenExpired(jwtToken)) {
            await store.dispatch('ui/setIsTokenExpired', true);
            return;
          }
        } catch (tokenCheckError) {
          // If we can't check the token, log the error but continue with other checks
          console.warn('Failed to check JWT token expiration:', tokenCheckError);
        }

        const extraType = error?.cause?.extraType;

        switch (extraType) {
          case ERROR_EXTRA_TYPE_CODES.NOT_FOUND:
          case ERROR_EXTRA_TYPE_CODES.FORBIDDEN_PRIVILEGE:
            await store.dispatch('ui/setIsRequestFailed', true);
            break;
          case ERROR_EXTRA_TYPE_CODES.FORBIDDEN_OUT_OF_ORG:
            await store.dispatch('ui/setIsAuthFailed', true);
            break;
          default:
            break;
        }
      },
    }),
  },
  mutations: {},
  actions: {},
  modules: {
    account,
    device,
    organization,
    notification,
    globalDialog,
    globalNotification,
    group,
    floorPlan,
    ui,
    archive,
    subscription,
    user,
    si,
    permission,
    role,
    alarm,
    externalDevice,
    accessControl,
    smartSensor,
    singleSignOn,
    mfa,
    auditLog,
    view,
    license,
    thirdParty,
  },
});

export default store;"}

