{
  "repositoryUrl": "https://github.com/VIVOTEK-IT/webtech-monorepo",
  "branches": [
    "*+([0-9])?(.{+([0-9]),x}).x",
    "main",
    "1.x.x"
  ],
  "plugins": [
    [
      "@semantic-release/commit-analyzer",
      {
        "preset": "angular",
        "releaseRules": [
          {
            "type": "docs",
            "scope": "README",
            "release": "patch"
          },
          {
            "type": "refactor",
            "release": "patch"
          },
          {
            "type": "style",
            "release": "patch"
          }
        ],
        "parserOpts": {
          "noteKeywords": [
            "BREAKING CHANGE",
            "BREAKING CHANGES",
            "BREAKING"
          ]
        }
      }
    ],
    "@semantic-release/release-notes-generator",
    [
      "@semantic-release/changelog",
      {
        "changelogFile": "CHANGELOG.md",
        "preset": "angular",
        "parserOpts": {
          "noteKeywords": [
            "BREAKING CHANGE",
            "BREAKING CHANGES",
            "BREAKING"
          ],
          "issuePrefixes": [
            "VOR-",
            "VP-"
          ],
          "referenceActions": "null"
        },
        "writerOpts": {
          "commitsSort": [
            "subject",
            "scope"
          ]
        }
      }
    ],
    [
      "@semantic-release/github",
      {
        "successComment": false,
        "failComment": false
      }
    ],
    [
      "@semantic-release/npm",
      {
        "npmPublish": false
      }
    ],
    [
      "@semantic-release/exec",
      {
        "verifyReleaseCmd": "chmod +x ./release/bump-sprint-number.sh && ./release/bump-sprint-number.sh ${nextRelease.type}",
        "prepareCmd": "node ./release/changelog.js",
        "successCmd": "chmod +x ./release/create-deploy-branch.sh && ./release/create-deploy-branch.sh"
      }
    ],
    [
      "@semantic-release/git",
      {
        "assets": [
          "docs",
          "package.json",
          "CHANGELOG.md"
        ],
        "message": "chore(release): ${nextRelease.version} [skip ci]\n\n${nextRelease.notes}"
      }
    ]
  ]
}

