const findFirstFreePlanRoute = (routes) => {
  const validRoutes = routes.filter((route) => route.path !== '' && !route.redirect);

  return validRoutes.reduce((found, route) => {
    if (found) {
      return found;
    }
    if (route.meta?.freePlanCanAccess && route.name) {
      return { name: route.name };
    }
    if (route.children) {
      return findFirstFreePlanRoute(route.children);
    }
    return null;
  }, null);
};

const SettingsRoute = [
  {
    path: '',
    redirect: () => {
      const isFreePlan = window.store.getters['organization/isFreePlan'];
      if (!isFreePlan) {
        return { name: 'OrganizationDetails' };
      }
      return findFirstFreePlanRoute(SettingsRoute) || { name: 'OrganizationDetails' };
    },
  },
  {
    path: 'organization',
    name: 'OrganizationDetails',
