    37→            <SvgIcon
    38→              v-if="page === 'system' && shouldShowSystemHintIcon"
    39→              class="warning-icon"
    40→              iconClass="general_hint_solid"
    41→              size="20px"
    42→            />
    43→          </component>
    44→        </template>
    45→      </div>
    46→
    47→      <div class="bottom-button-list">
    48→        <div
    49→          v-if="shouldShowUpgradeInfo"
    50→          class="upgrade-info"
    51→        >
    52→          <h5 class="upgrade-info-title">
    53→            {{ upgradeInfo.title }}
    54→          </h5>
    55→
    56→          <div class="upgrade-info-description">
    57→            {{ upgradeInfo.description }}
    58→          </div>
    59→
    60→          <div>
    61→            <AppUpgradeButton
    62→              :text="upgradeInfo.buttonText"
    63→              class="upgrade-info-button"
    64→              @click="goToUpgradePlan"
    65→            />
    66→          </div>
    67→        </div>
    68→
    69→        <div>
    70→          <div
    71→            v-for="(button, index) in bottomButtons"
    72→            :key="index"
    73→            class="navigation-button"
    74→            @click="buttonClickedCallbackMap[button]()"
    75→          >
    76→            <SvgIcon
    77→              :iconClass="translateButtonIcon(button)"
    78→              size="32px"
    79→            />
    80→            <div class="label">
    81→              <h5>
    82→                {{ buttomLabelMap[button] }}
    83→              </h5>
    84→            </div>
    85→          </div>
    86→        </div>
    87→      </div>
    88→    </div>
    89→  </div>
    90→</template>
    91→
    92→<script>
    93→import { mapState, mapGetters } from 'vuex';
    94→
    95→import { NAVIGATION_TRACKING_MAP } from '@vivotek/const-vsaas';
    96→import RemoteConfigService, { PORTAL_FEATURE_CONFIG_KEY } from '@vivotek/lib-remote-config';
    97→import VendorHelper from '@vivotek/lib-vendor-helper';
    98→
    99→import AppUpgradeButton from '@/components/Buttons/AppUpgradeButton.vue';
   100→import OrganizationNavDropdown from '@/components/Dropdowns/OrganizationNavDropdown.vue';
   101→import { PAGES, PAGE_LIST } from '@/constants/Page';
   102→
   103→export default {
   104→  name: 'Navigation',
   105→  components: {
   106→    OrganizationNavDropdown,
   107→    AppUpgradeButton,
   108→  },
   109→  data() {
   110→    return {
   111→      indexPage: 'liveview',
   112→      bottomButtons: ['themes'],
   113→      showNavigationBar: false,
   114→      isResellerShow: false,
   115→      isCustomizeViewEnabled: false,
   116→      isFloorplanDarkReleaseEnabled: false,
   117→      NAVIGATION_TRACKING_MAP,
   118→    };
   119→  },
   120→  methods: {
   121→    shouldDisableLink(page) {
   122→      return !this.isLicenseGranted(page) || !this.canPlanUpdate(`/${page}`);
   123→    },
   124→    shouldDisplayLink(page) {
   125→      /**
   126→       * profile search depends on ai control settings, if user never update FR it should be enabled
   127→       * and if user update FR it should according to ai control settings enable or disable
   128→       */
   129→      if (page === 'profile-search') {
   130→        return (
   131→          this.aiControlPermissionManager.isFacialRecognitionEnable &&
   132→          this.hasPlanPermission(`/${page}`) &&
   133→          this.hasPagePermission(`/${page}`)
   134→        );
   135→      }
   136→      return this.hasPlanPermission(`/${page}`) && this.hasPagePermission(`/${page}`);
   137→    },
   138→    switchTheme() {
   139→      if (document.body.classList.contains('light')) {
   140→        this.$theme.set('dark');
   141→      } else {
   142→        this.$theme.set('light');
   143→      }
   144→    },
   145→    setShowNavigationBar(value) {
   146→      if (this.showNavigationBar === value) {
   147→        return;
   148→      }
   149→      if (!value) {
   150→        this.$refs.OrganizationNavDropdown.onDropdownClickaway();
   151→      }
   152→      this.showNavigationBar = value;
   153→    },
   154→    goToUpgradePlan() {
   155→      this.$router.push({ name: 'UpgradePlan' });
   156→    },
   157→    getNavigationLink(page) {
   158→      return this.isLicenseGranted(page) ? `/${page}` : null;
   159→    },
   160→    getNavigationComponent(page) {
   161→      return this.isLicenseGranted(page) ? 'router-link' : 'div';
   162→    },
   163→    getTrackingConfig(page) {
   164→      return this.isLicenseGranted(page) ? { eventName: NAVIGATION_TRACKING_MAP[page] } : {};
   165→    },
   166→  },
   167→  computed: {
   168→    ...mapState('permission', ['aiControlPermissionManager']),
   169→    ...mapGetters('permission', ['hasPagePermission', 'hasPlanPermission', 'canPlanUpdate']),
   170→    ...mapGetters('organization', ['isLicenseGranted', 'isFreePlan']),
   171→    ...mapGetters('license', ['shouldShowLicenseInformationHint']),
   172→    ...mapGetters('si', ['getResellerInformation']),
   173→    ...mapGetters('account', ['isOwner', 'isAdmin']),
   174→    ...mapGetters('organization', ['supportList']),
   175→    pageList() {
   176→      return PAGE_LIST.filter((page) => {
   177→        if (page === 'VIEW' && !this.isCustomizeViewEnabled) {
   178→          return false;
   179→        }
   180→        if (page === 'FLOORPLAN' && !this.isFloorplanDarkReleaseEnabled) {
   181→          return false;
   182→        }
   183→        return true;
   184→      });
   185→    },
   186→    shouldShowSystemHint() {
   187→      return (
   188→        (this.isResellerShow && !this.getResellerInformation) ||
   189→        (!this.isFreePlan && this.shouldShowLicenseInformationHint)
   190→      );
   191→    },
   192→    shouldShowSystemHintIcon() {
   193→      return this.showNavigationBar && this.shouldShowSystemHint;
   194→    },
   195→    icons() {
   196→      return this.pageList.reduce((obj, cur) => {
   197→        if (PAGES[cur].path === 'system' && !this.showNavigationBar && this.shouldShowSystemHint) {
   198→          return { ...obj, [PAGES[cur].path]: 'settings_hint_solid' };
   199→        }
   200→        return { ...obj, [PAGES[cur].path]: PAGES[cur].icon };
   201→      }, {});
   202→    },
   203→    pageLabelMap() {
   204→      const $t = this.$t.bind(this);
   205→      return PAGE_LIST.reduce((obj, cur) => ({ ...obj, [PAGES[cur].path]: PAGES[cur].i18n($t) }), {});
   206→    },
   207→    pages() {
   208→      return this.pageList.map((pageName) => PAGES[pageName].path);
   209→    },
   210→    buttonClickedCallbackMap() {
   211→      return {
   212→        themes: this.switchTheme,
   213→      };
   214→    },
   215→    buttomLabelMap() {
   216→      return {
   217→        themes: this.$t('Themes'),
   218→      };
   219→    },
   220→    translateButtonIcon() {
   221→      return (icon) => {
   222→        if (icon === 'themes') {
   223→          return this.$theme.value === 'light' ? 'general_mode_dark' : 'general_mode_light';
   224→        }
   225→        return `general_${this.icons[icon]}`;
   226→      };
   227→    },
   228→    shouldShowUpgradeInfo() {
   229→      return this.showNavigationBar && this.isFreePlan && !VendorHelper.isMarchNetworks;
   230→    },
   231→    upgradeInfo() {
   232→      return {
   233→        title: this.$t('Upgrade your license plan!'),
   234→        description: this.$t('Explore {productName} license plans and latest package with features', {
   235→          productName: VendorHelper.getVendorConfig().PRODUCT_NAME,
   236→        }),
   237→        buttonText: this.$t('Explore plans'),
   238→      };
   239→    },
   240→  },
   241→  async created() {
   242→    this.isCustomizeViewEnabled = RemoteConfigService.getConfiguration(PORTAL_FEATURE_CONFIG_KEY.CUSTOMIZED_VIEW);
   243→    this.isResellerShow = RemoteConfigService.getConfiguration(PORTAL_FEATURE_CONFIG_KEY.ADD_RESELLER_HINT);
   244→    this.isFloorplanDarkReleaseEnabled = this.supportList.includes('FloorPlan');
   245→  },
   246→};
   247→</script>
