   275→
   276→    .top-button-list {
   277→      > :deep(a) {
   278→        &:hover {
   279→          color: @color-icon04;
   280→          background-color: @color-navi-surface02;
   281→          h5 {
   282→            color: @color-text01;
   283→            font-weight: 600; // semi-bold // 影響不了內部的 h5
   284→          }
   285→          :deep(.svg-icon) {
   286→            color: @color-icon04;
   287→          }
   288→        }
   289→        &.active,
   290→        &.router-link-active,
   291→        &:active {
   292→          h5 {
   293→            color: @color-text09;
   294→            font-weight: 600; // semi-bold // 影響不了內部的 h5
   295→          }
   296→          background-color: @color-primary;
   297→          :deep(.svg-icon) {
   298→            color: @color-icon05;
   299→          }
   300→        }
   301→      }
   302→    }
   303→
   304→    .navigation-button {
   305→      height: 48px;
   306→      display: flex;
   307→      align-items: center;
   308→      box-sizing: border-box;
   309→      cursor: pointer;
   310→      .org-icon {
   311→        width: 32px;
