import { APP_TABLE_STATUS } from '@vivotek/generic-vue-components';

import actions from './actions';
import getters from './getters';
import mutations from './mutations';

const alarm = {
  namespaced: true,
  state: {
    alarms: [],
    status: APP_TABLE_STATUS.INITIAL,
    activeAlarmID: null,
    webhookProfileMap: {},
    // Track which alarms have S3 source data loaded (Object for reactivity)
    loadedAlarmSourceIds: {},
    // Track which alarms are currently loading S3 source data (Object for reactivity)
    loadingAlarmSourceIds: {},
