const mutations = {
  setAlarms(state, alarms) {
    if (!alarms) {
      return;
    }

    state.alarms = alarms;
    // Clear load state when alarms are refreshed
    state.loadedAlarmSourceIds = {};
    state.loadingAlarmSourceIds = {};
  },
  createAlarm(state, alarm) {
    state.alarms.push(alarm);
  },
  deleteAlarm(state, alarmID) {
    state.alarms = state.alarms.filter((alarm) => alarm.alarmID !== alarmID);
  },
  setAlarmEnable(state, { alarmID, enable }) {
    const alarm = state.alarms.find(($_alarm) => $_alarm.alarmID === alarmID);

    if (alarm) {
      alarm.enable = enable;
    }
  },
  setActiveAlarmID(state, alarmID) {
    state.activeAlarmID = alarmID;
  },
  setStatus(state, status) {
    state.status = status;
  },
  addWebhookProfile(state, webhookProfiles) {
    state.webhookProfileMap[webhookProfiles.webhookID] = webhookProfiles;
  },
  restoreWebhookProfile(state) {
    state.webhookProfileMap = {};
  },
  deleteWebhookProfile(state, webhookID) {
    delete state.webhookProfileMap[webhookID];
  },
  removeAlarmBindingWithAlarmID(state, alarmID) {
    Object.values(state.webhookProfileMap).forEach((profile) => {
      if (profile.alarmIDBindingsMap[alarmID]) {
        profile.removeAlarmIDBindings(alarmID);
        profile.update();
      }
    });
  },
  // Alarm source loading state mutations
  setAlarmSourceLoading(state, alarmIds) {
    const newLoadingIds = { ...state.loadingAlarmSourceIds };
    alarmIds.forEach((id) => {
      newLoadingIds[id] = true;
    });
    state.loadingAlarmSourceIds = newLoadingIds;
  },
  setAlarmSourceLoaded(state, alarmIds) {
    const newLoadingIds = { ...state.loadingAlarmSourceIds };
    const newLoadedIds = { ...state.loadedAlarmSourceIds };
    alarmIds.forEach((id) => {
      delete newLoadingIds[id];
      newLoadedIds[id] = true;
    });
    state.loadingAlarmSourceIds = newLoadingIds;
    state.loadedAlarmSourceIds = newLoadedIds;
  },
  clearAlarmSourceLoadState(state) {
    state.loadedAlarmSourceIds = {};
    state.loadingAlarmSourceIds = {};
  },
};

export default mutations;


