export default {
  alarmCount(state) {
    return Object.values(state.alarms).length;
  },
  activeAlarm(state) {
    return state.alarms.find((alarm) => alarm.alarmID === state.activeAlarmID);
  },
  getAlarmWithAlarmID(state) {
    return (alarmID) => state.alarms.find((alarm) => alarm.alarmID === alarmID);
  },
  webhookProfiles(state) {
    return Object.values(state.webhookProfileMap);
  },
  alarmIDBindingsMap(state, getters) {
    return Object.values(state.alarms).reduce((acc, alarm) => {
      const target = getters.webhookProfiles.find((profile) => profile.alarmIDBindingsMap?.[alarm.alarmID]);
      if (!target) {
        return acc;
      }
      acc[alarm.alarmID] = target;
      return acc;
    }, {});
  },
  hasUnsetCityAlarm(state) {
    return state.alarms.some((alarm) => !alarm.city);
  },
  isAlarmSourceLoaded(state) {
    return (alarmId) => !!state.loadedAlarmSourceIds[alarmId];
  },
  isAlarmSourceLoading(state) {
    return (alarmId) => !!state.loadingAlarmSourceIds[alarmId];
  },
  areAllSourcesLoaded(state) {
    return (alarmIds) => alarmIds.every((id) => !!state.loadedAlarmSourceIds[id]);
  },
};


