import { PROFILE_TEMPLATE_FORM } from '@vivotek/const-vsaas';
import Context from '@vivotek/context';
import { APP_TABLE_STATUS } from '@vivotek/generic-vue-components';

import Alarm from '@/models/Alarm/Alarm';
import WebhookProfile from '@/models/Webhook/WebhookProfile';

const actions = {
  async fetchAlarmSettings({ state, commit, rootGetters, rootState }) {
    const { organizationID } = rootGetters['organization/currentOrganization'];
    const provider = rootGetters['accessControl/currentProvider'];
    const isFirstQuery = state.status === APP_TABLE_STATUS.INITIAL;
    const devicesMap = rootGetters['device/allDevicesMap'];

    commit('setStatus', APP_TABLE_STATUS.LOADING);
    try {
      const res = await rootState.apiService.queryOrganizationAlarmSetting({ organizationID });
      commit(
        'setAlarms',
        res?.map((alarm) => Context.provide(Alarm, { ...alarm, organizationID, provider }, devicesMap)),
      );
      commit('setStatus', APP_TABLE_STATUS.NO_ALARM_SETTINGS);
    } catch (error) {
      commit('setStatus', isFirstQuery ? APP_TABLE_STATUS.INITIAL : APP_TABLE_STATUS.NO_ALARM_SETTINGS);
    }
  },
  async createAlarm({ commit, rootGetters, rootState }, payload) {
    try {
      const { organizationID } = rootGetters['organization/currentOrganization'];
      const provider = rootGetters['accessControl/currentProvider'];
      const devicesMap = rootGetters['device/allDevicesMap'];

      const {
        recipients,
        source,
        accessControlSource,
        smartSensorSource,
        doSettings,
        webhook,
        networkSpeakers,
        audioDeterrentCameras,
        ...alarmData
      } = payload;
      const response = await rootState.apiService.createOrganizationAlarmSetting({
        organizationID,
        ...alarmData,
      });

      const { alarmID, putSettingsURL, getSettingsURL } = response;
      const createdBy = rootState.account.attributes.nickname;
      const alarm = Context.provide(
        Alarm,
        {
          ...payload,
          alarmID,
          createdBy,
          putSettingsURL,
          getSettingsURL,
          organizationID,
          enable: true,
          provider,
        },
        devicesMap,
      );
      await alarm.updateS3Object({
        recipients,
        source,
        accessControlSource,
        smartSensorSource,
        doSettings,
        webhook,
        networkSpeakers,
        audioDeterrentCameras,
      });

      commit('createAlarm', alarm);
      return alarmID;
    } catch (error) {
      throw new Error(`error createAlarm: ${error.toString()}`);
    }
  },
  async deleteAlarm({ commit, rootState }, alarmIDs) {
    try {
      await rootState.apiService.deleteOrganizationAlarmSettings(alarmIDs);
      alarmIDs.forEach((alarmID) => {
        commit('deleteAlarm', alarmID);
        commit('removeAlarmBindingWithAlarmID', alarmID);
      });
    } catch (error) {
      throw new Error(`error deleteAlarm: ${error.toString()}`);
    }
  },
  async switchAlarmEnable({ commit, rootState }, { alarmID, enable }) {
    try {
      const res = await rootState.apiService.switchOrganizationAlarmSetting({
        alarmID,
        enable,
      });
      commit('setAlarmEnable', { alarmID, enable });
      return res;
    } catch (error) {
      throw error.generalize();
    }
  },
  async queryWebhookProfile({ commit, rootState, rootGetters }, { $t }) {
    const { organizationID } = rootGetters['organization/currentOrganization'];

    const response = await rootState.apiService.queryWebhook({ organizationID, $t });

    response.forEach((profile) => {
      const instance = Context.provide(WebhookProfile, profile, $t);
      commit('addWebhookProfile', instance);
    });
  },
  addWebhookProfile({ commit }, { $t, webhookProfile, profileTemplate }) {
    if (webhookProfile) {
      commit('addWebhookProfile', webhookProfile);
      return;
    }

    const instance = Context.provide(
      WebhookProfile,
      profileTemplate ? PROFILE_TEMPLATE_FORM[profileTemplate]($t) : null,
      $t,
    );
    commit('addWebhookProfile', instance);
  },
  async deleteWebhookProfile({ state, commit, rootState }, webhookID) {
    if (webhookID === 'temp') {
      commit('deleteWebhookProfile', 'temp');
      return;
    }

    try {
      const instance = state.webhookProfileMap[webhookID];

      await instance.delete();
      commit('deleteWebhookProfile', webhookID);
    } catch (error) {
      throw new Error(`error deleteWebhookProfile: ${error.toString()}`);
    }
  },
  removeAlarmBindingWithAlarmID({ commit }, alarmID) {
    commit('removeAlarmBindingWithAlarmID', alarmID);
  },
  async resetOrganizationAlarmSettingsTimeZone({ rootState, rootGetters }, { city }) {
    try {
      const { organizationID } = rootGetters['organization/currentOrganization'];
      await rootState.apiService.resetOrganizationAlarmSettingsTimeZone({ organizationID, city });
    } catch (error) {
      throw new Error(`error resetOrganizationAlarmSettingsTimeZone: ${error.toString()}`);
    }
  },
  /**
   * Batch fetch S3 source data for multiple alarms
   * Used for lazy loading alarm sources based on pagination
   * @param {Array<string>} alarmIds - Array of alarm IDs to fetch source data for
   * @param {number} chunkSize - Number of concurrent requests per batch (default: 5)
   */
  async batchFetchAlarmSources({ state, commit, getters }, alarmIds, chunkSize = 5) {
    const alarmsToFetch = alarmIds.filter(
      (id) => !getters.isAlarmSourceLoaded(id) && !getters.isAlarmSourceLoading(id),
    );

    if (alarmsToFetch.length === 0) {
      return;
    }

    commit('setAlarmSourceLoading', alarmsToFetch);

    for (let i = 0; i < alarmsToFetch.length; i += chunkSize) {
      const chunk = alarmsToFetch.slice(i, i + chunkSize);

      // eslint-disable-next-line no-await-in-loop -- intentional: wait for each chunk before starting next
      await Promise.allSettled(
        chunk.map((alarmId) => {
          const alarm = state.alarms.find((a) => a.alarmID === alarmId);
          return alarm?.queryS3Object?.() || Promise.resolve();
        }),
      );

      commit('setAlarmSourceLoaded', chunk);
    }
  },
};

export default actions;


